  border: 1px solid #e2e8f0;
  border-radius: 8px;
  padding: 16px;
  background: white;
}

.option-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}

.option-header label {
  font-weight: 600;
  color: #374151;
  font-size: 14px;
}

.option-controls {
  display: flex;
  align-items: center;
  gap: 12px;
}

.correct-answer-label {
  display: flex;
  align-items: center;
  gap: 6px;
  cursor: pointer;
  font-size: 12px;
  color: #059669;
  font-weight: 500;
}

.correct-answer-label input[type="radio"] {
  width: 16px;
  height: 16px;
  accent-color: #059669;
}

.remove-option-btn {
  background: #fee2e2;
  color: #dc2626;
  border: 1px solid #fecaca;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 11px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.remove-option-btn:hover {
  background: #fecaca;
  border-color: #f87171;
}

.add-option-btn {
  background: #dbeafe;
  color: #1d4ed8;
  border: 2px dashed #93c5fd;
  padding: 12px 20px;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  width: 100%;
  margin-top: 12px;
}

.add-option-btn:hover {
  background: #bfdbfe;
  border-color: #60a5fa;
}

.form-group small {
  display: block;
  margin-top: 4px;
  color: #6b7280;
  font-size: 12px;
}

/* Enhanced Question Card */
.question-info h4 {
  color: #2d3748;
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px 0;
  line-height: 1.4;
}

.question-details {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 12px;
  margin-top: 16px;
}

.detail-item {
  padding: 8px 12px;
  background: #f8fafc;
  border-radius: 6px;
  border: 1px solid #e2e8f0;
  font-size: 13px;
}

.detail-item strong {
  color: #374151;
  font-weight: 600;
}

/* Modal enhancements for questions */
.large-modal .modal-content {
  max-width: 900px;
  max-height: 95vh;
}

/* Expanded Question View */
.question-expanded {
  margin-top: 20px;
  padding-top: 20px;
  border-top: 1px solid #e2e8f0;
}

.question-expanded h5 {
  color: #374151;
  font-size: 14px;
  font-weight: 600;
  margin: 0 0 8px 0;
}

.question-full-text {
  margin-bottom: 20px;
}

.question-full-text p {
  color: #4b5563;
  line-height: 1.6;
  margin: 0;
  padding: 12px;
  background: #f9fafb;
  border-radius: 8px;
  border-left: 4px solid #667eea;
}

.question-options {
  margin-bottom: 20px;
}

.option-display {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  margin-bottom: 6px;
  border-radius: 6px;
  border: 1px solid #e5e7eb;
  background: white;
  transition: all 0.2s ease;
}

.option-display.correct-option {
  background: #f0fdf4;
  border-color: #22c55e;
  border-width: 2px;
}

.option-label {
  font-weight: 600;
  color: #374151;
  min-width: 20px;
}

.option-text {
  flex: 1;
  color: #4b5563;
}

.correct-indicator {
  background: #22c55e;
  color: white;
  padding: 2px 8px;
  border-radius: 12px;
  font-size: 11px;
  font-weight: 600;
}

.question-explanation {
  margin-bottom: 0;
}

.question-explanation p {
  color: #4b5563;
  line-height: 1.6;
  margin: 0;
  padding: 12px;
  background: #fef3c7;
  border-radius: 8px;
  border-left: 4px solid #f59e0b;
}

