  .start-test-btn,
  .view-result-btn {
    width: 100%;
    justify-content: center;
  }

  .test-stats {
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
  }

  .stat-item {
    padding: 8px;
  }

  .stat-value {
    font-size: 16px;
  }
}

@media (max-width: 480px) {
  .test-stats {
    grid-template-columns: repeat(2, 1fr);
  }

  .test-card-header {
    flex-direction: column;
    gap: 10px;
    align-items: flex-start;
  }

  .history-header {
    flex-direction: column;
    gap: 10px;
    align-items: flex-start;
  }
}

