.instructions-container {
  max-width: 1000px;
  margin: 0 auto;
  padding: 20px;
  background-color: #f8f9fa;
  min-height: 100vh;
}

.instructions-header {
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 30px;
  padding: 20px;
  background: white;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.back-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  background: #6c757d;
  color: white;
  border: none;
  padding: 10px 16px;
  border-radius: 8px;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}

.back-btn:hover {
  background: #5a6268;
}

.instructions-header h1 {
  margin: 0;
  color: #2c3e50;
  font-size: 28px;
  font-weight: 600;
}

.instructions-content {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

.test-overview {
  background: white;
  padding: 25px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.test-overview h2 {
  margin: 0 0 20px 0;
  color: #2c3e50;
  font-size: 20px;
  font-weight: 600;
}

.overview-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
}

.overview-item {
  display: flex;
  align-items: center;
  gap: 15px;
  padding: 15px;
  background: #f8f9fa;
  border-radius: 10px;
  border-left: 4px solid #667eea;
}

.overview-icon {
  font-size: 20px;
  color: #667eea;
}

.marks-icon,
.type-icon {
  font-size: 20px;
}

.overview-item div {
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.overview-label {
  font-size: 12px;
  color: #6c757d;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.overview-value {
  font-size: 16px;
  font-weight: 600;
  color: #2c3e50;
}

.sections-overview {
  background: white;
  padding: 25px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.sections-overview h2 {
  margin: 0 0 20px 0;
  color: #2c3e50;
  font-size: 20px;
  font-weight: 600;
}

.sections-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 15px;
}

.section-card {
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  padding: 20px;
  border-radius: 10px;
  border: 1px solid #e9ecef;
  transition: transform 0.2s ease;
}

.section-card:hover {
  transform: translateY(-2px);
}

.section-card h3 {
  margin: 0 0 15px 0;
  color: #2c3e50;
  font-size: 18px;
  font-weight: 600;
  text-align: center;
}

.section-details {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.section-details span {
  font-size: 14px;
  color: #495057;
  padding: 4px 0;
}

.test-instructions {
  background: white;
  padding: 25px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.test-instructions h2 {
  margin: 0 0 20px 0;
  color: #2c3e50;
  font-size: 20px;
  font-weight: 600;
}

.instructions-text,
.default-instructions {
  color: #495057;
  line-height: 1.6;
}

.default-instructions h3 {
  color: #2c3e50;
  font-size: 16px;
  font-weight: 600;
  margin: 25px 0 15px 0;
}

.default-instructions ul {
  margin: 0 0 20px 0;
  padding-left: 20px;
}

.default-instructions li {
  margin-bottom: 8px;
  font-size: 14px;
}

.important-note {
  background: linear-gradient(135deg, #fff3cd 0%, #ffeaa7 100%);
  border: 1px solid #ffc107;
  padding: 15px;
  border-radius: 8px;
  margin-top: 20px;
  font-size: 14px;
  color: #856404;
}

.agreement-section {
  background: white;
  padding: 25px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
  border: 2px solid #e9ecef;
}

.agreement-checkbox {
  display: flex;
  align-items: flex-start;
  gap: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1.5;
  color: #2c3e50;
  position: relative;
}

.agreement-checkbox input[type="checkbox"] {
  display: none;
}

.checkmark {
  width: 20px;
  height: 20px;
  background-color: #fff;
  border: 2px solid #ddd;
  border-radius: 4px;
  flex-shrink: 0;
  margin-top: 2px;
  position: relative;
  transition: all 0.2s ease;
}

.agreement-checkbox:hover .checkmark {
  border-color: #667eea;
}

.agreement-checkbox input[type="checkbox"]:checked + .checkmark {
  background-color: #667eea;
  border-color: #667eea;
}

.agreement-checkbox input[type="checkbox"]:checked + .checkmark::after {
  content: 'âœ“';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-size: 12px;
  font-weight: bold;
}

.start-test-section {
  display: flex;
  justify-content: center;
  padding: 30px 0;
}

.start-test-btn {
  background: linear-gradient(135deg, #28a745 0%, #20c997 100%);
  color: white;
  border: none;
  padding: 15px 40px;
  border-radius: 30px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(40, 167, 69, 0.3);
}

.start-test-btn:hover:not(.disabled) {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(40, 167, 69, 0.4);
}

.start-test-btn.disabled {
  background: #6c757d;
  cursor: not-allowed;
  box-shadow: none;
}

.loading-spinner {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 300px;
  gap: 20px;
}

.spinner {
  width: 40px;
  height: 40px;
  border: 3px solid #e9ecef;
  border-top: 3px solid #667eea;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.error-message {
  text-align: center;
  background: white;
  padding: 40px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
  margin-top: 50px;
}

.error-message h2 {
  color: #dc3545;
  margin: 0 0 15px 0;
}

.error-message p {
  color: #6c757d;
  margin: 0 0 25px 0;
}

.error-message button {
  background: #667eea;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  cursor: pointer;
  font-size: 14px;
}

/* Responsive Design */
@media (max-width: 768px) {
  .instructions-container {
    padding: 15px;
  }

  .instructions-header {
    flex-direction: column;
    align-items: flex-start;
    gap: 15px;
  }

  .instructions-header h1 {
    font-size: 24px;
  }

  .overview-grid {
    grid-template-columns: 1fr;
  }

  .sections-grid {
    grid-template-columns: 1fr;
  }

  .overview-item {
    flex-direction: column;
    align-items: flex-start;
    text-align: left;
  }

  .overview-item div {
    align-items: flex-start;
  }

  .test-overview,
  .sections-overview,
  .test-instructions,
  .agreement-section {
    padding: 20px;
  }

  .start-test-btn {
    width: 100%;
    justify-content: center;
  }
}

@media (max-width: 480px) {
  .instructions-header h1 {
    font-size: 20px;
  }

  .test-overview h2,
  .sections-overview h2,
  .test-instructions h2 {
    font-size: 18px;
  }

  .default-instructions h3 {
    font-size: 15px;
  }

  .agreement-checkbox {
    font-size: 13px;
  }

  .start-test-btn {
    padding: 12px 30px;
    font-size: 15px;
  }
}

