/* Container */
.SNAP-wrap {
  max-width: 1080px;
  margin: 32px auto 64px;
  padding: 0 20px;
  font-family: "Segoe UI", sans-serif;
  color: #1c1c1c;
}

/* Title */
.SNAP-title {
  font-size: 40px;
  line-height: 1.2;
  font-weight: 700;
  margin-bottom: 16px;
  color: #0b0b0b;
}

/* Video (responsive) */
.SNAP-video {
  position: relative;
  width: 100%;
  aspect-ratio: 16 / 9;
  background: #000;
  border-radius: 12px;
  overflow: hidden;
  margin: 14px 0 22px;
}
.SNAP-video iframe {
  width: 100%;
  height: 100%;
  border: 0;
  display: block;
}

/* Headings & paragraphs */
.SNAP-h2 {
  font-size: 26px;
  font-weight: 700;
  margin: 26px 0 10px;
  color: #0b278f;
}
.SNAP-p {
  font-size: 16px;
  line-height: 1.7;
  margin-bottom: 12px;
}

/* Lists */
.SNAP-list,
.SNAP-olist {
  margin: 8px 0 14px 20px;
  font-size: 16px;
  line-height: 1.7;
}

/* Notes & muted */
.SNAP-muted {
  font-size: 14px;
  color: #666;
}
.SNAP-note {
  margin: 16px 0 22px;
  padding: 12px 16px;
  background: #f8f8ff;
  border-left: 4px solid #0b278f;
  border-radius: 6px;
  font-size: 15px;
  line-height: 1.6;
}

/* Mobile */
@media (max-width: 520px) {
  .SNAP-title { font-size: 30px; }
  .SNAP-h2 { font-size: 22px; }
  .SNAP-p, .SNAP-list, .SNAP-olist { font-size: 15px; }
}

