    const fetchPredictionData = async () => {
        try {
            const response = await axios.get(`/api/v2/iim-predictor/${userId}`);

            console.log("âœ… API Response Data:", response.data); // ðŸ” Debugging line

            setPredictionData(response.data);
            setLoading(false);
        } catch (error) {
            console.error("âŒ Error fetching prediction data:", error);
            setLoading(false);
        }
    };

    fetchPredictionData();
}, [userId]);


  if (loading) {
    return <div className="loading-spinner">Loading results...</div>;
  }

  // âœ… Load More Function
  const handleLoadMore = () => {
    setVisibleColleges((prev) => prev + 5);
  };

  // âœ… Show Less Function
  const handleShowLess = () => {
    setVisibleColleges(5);
  };

  return (
    <div>
    <div className="prediction-container">
      <h2>ðŸŽ‰ Congratulations {predictionData.name}!</h2>
      <p>
        Your profile matches <b>{predictionData.matchedSchools}</b> Top
        B-Schools
      </p>

      <div className="user-summary">
        <table>
          <thead>
            <tr>
              <th>NAME</th>
              <th>CATEGORY</th>
              <th>CLASS X</th>
              <th>CLASS XII</th>
              <th>GRADUATION</th>
              <th>WORK EXP.</th>
            </tr>
          </thead>
          <tbody>
          <tr>
    <td>{predictionData.name}</td>  {/* âœ… Yeh name ab database se aa raha hai */}
    <td>{predictionData.category}</td>
    <td>{predictionData.classX}%</td>
    <td>{predictionData.classXII}%</td>
    <td>{predictionData.graduation} ({predictionData.gradPercentage}%)</td>
    <td>{predictionData.workExperience} months</td>
</tr>
          </tbody>
        </table>
      </div>

      <div className="prediction-container">
        <h2>B-Schools You Can Crack ðŸŽ“</h2>
        <p>
          Based upon your profile details and the criteria for shortlisting used
          by different IIMs.
        </p>

        <table className="college-table">
          <thead>
            <tr>
              <th>COLLEGE NAME</th>
              <th>HIGHEST PACKAGE</th>
              <th>AVG. PACKAGE</th>
              <th>YOUR STATUS</th>
              <th>EXAMS</th>
            </tr>
          </thead>
          <tbody>
            {collegeData.slice(0, visibleColleges).map((college, index) => (
              <tr key={index}>
                <td>{college.name}</td>
                <td>{college.highestPackage}</td>
                <td>{college.avgPackage}</td>
                <td
                  className={
                    college.status.includes("Good")
                      ? "status-good"
                      : "status-moderate"
                  }
                >
                  {college.status}
                </td>
                <td>{college.exam}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* âœ… Buttons for Load More & Show Less */}
        <div className="button-container">
          {visibleColleges < collegeData.length && (
            <button onClick={handleLoadMore} className="load-more-btn">
              Load More
            </button>
          )}
          {visibleColleges > 5 && (
            <button onClick={handleShowLess} className="show-less-btn">
              Show Less
            </button>
          )}
        </div>
      </div>

      <button
        onClick={() => {
          localStorage.removeItem(`iim-predictor-${userId}`);
          window.location.href = "/";
        }}
        className="reset-btn"
      >
        ðŸ”„ Recalculate
      </button>
      </div>


      <div>
      <IIMPredictionpage/>
      </div>
    </div>
  );
};

export default IIMPredictorResult;

