      const token = localStorage.getItem("adminToken");
      await axios.delete(`/api/chapters/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setChapters((prev) => prev.filter((c) => c._id !== id));
    } catch (err) {
      console.error("Delete failed", err);
    }
  };

  // âœ… Update chapter
  const handleUpdate = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      await axios.put(`/api/chapters/${editingChapter._id}`, {
        name: editChapterName,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Chapter updated âœ…");
      setEditingChapter(null);
      setEditChapterName("");
     fetchChapters(selectedSubjectId);
    } catch (err) {
      console.error("Update failed", err);
    }
  };

  return (
    <div className="chapter-tab">
      <h3>ðŸ“˜ Chapter Management</h3>

      {/* Subject Dropdown */}
      <div className="form-group">
        <label>Select Subject</label>
        <select
          value={selectedSubjectId}
          onChange={(e) => setSelectedSubjectId(e.target.value)}
        >
          <option value="">-- Select Subject --</option>
          {subjects.map((sub) => (
            <option key={sub._id} value={sub._id}>
              {sub.name}
            </option>
          ))}
        </select>
      </div>

      {/* Add Chapter */}
      {selectedSubjectId && (
        <>
          <div className="form-group">
            <label>New Chapter Name</label>
            <input
              type="text"
              value={chapterName}
              onChange={(e) => setChapterName(e.target.value)}
            />
            <button className="add-btn" onClick={handleAddChapter}>âž• Add Chapter</button>
          </div>

          <div className="table-wrapper">
            <h4>Chapter List</h4>
            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Chapter</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {chapters.map((ch, index) => (
                  <tr key={ch._id}>
                    <td>{index + 1}</td>
                    <td>{ch.name}</td>
                    <td className="icon-actions">
                      <FaEdit
                        title="Edit"
                        style={{ cursor: "pointer", marginRight: "10px", color: "#007bff" }}
                        onClick={() => {
                          setEditingChapter(ch);
                          setEditChapterName(ch.name);
                        }}
                      />
                      <FaTrash
                        title="Delete"
                        style={{ cursor: "pointer", color: "red" }}
                        onClick={() => handleDelete(ch._id)}
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}

      {/* Edit Chapter Modal */}
      {editingChapter && (
        <div className="edit-modal">
          <div className="edit-modal-content">
            <h4>Edit Chapter</h4>
            <input
              type="text"
              value={editChapterName}
              onChange={(e) => setEditChapterName(e.target.value)}
            />
            <button onClick={handleUpdate}>Update</button>
            <button onClick={() => setEditingChapter(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChapterTab;

backend1/models/LiveClass.js.content:
const mongoose = require('mongoose');

const LiveClassSchema = new mongoose.Schema({
  title: { type: String, required: true, trim: true },
  courseId: { type: mongoose.Schema.Types.ObjectId, ref: 'Course', required: true, index: true },
  platform: { type: String, enum: ['zoom', 'google_meet', 'custom'], required: true },
  joinLink: { type: String, required: true },
  startTime: { type: Date, required: true, index: true },
  endTime: { type: Date, required: true },
  timezone: { type: String, default: 'Asia/Kolkata' },
  rrule: { type: String, default: '' },
  description: { type: String, default: '' },
  reminders: { type: [Number], default: [1440, 60, 10] }, // minutes before: 24h, 1h, 10m
  status: { type: String, enum: ['scheduled', 'live', 'completed', 'cancelled'], default: 'scheduled' },
  platformMeta: {
    meetingId: { type: String, default: '' },
    hostUrl: { type: String, default: '' }
  },
  notifiedStudents: [{ type: mongoose.Schema.Types.ObjectId, ref: 'User' }],
  notificationSent: { type: Boolean, default: false },
  createdBy: {
    id: { type: mongoose.Schema.Types.ObjectId, required: true },
    role: { type: String, enum: ['admin', 'subadmin'], required: true }
  }
}, { timestamps: true });

LiveClassSchema.pre('save', function(next) {
  try {
    const now = new Date();
    if (this.status !== 'cancelled') {
      if (now < this.startTime) this.status = 'scheduled';
      else if (now >= this.startTime && now <= this.endTime) this.status = 'live';
      else if (now > this.endTime) this.status = 'completed';
    }
  } catch (_) {}
  next();
});

LiveClassSchema.index({ courseId: 1, startTime: 1 });

module.exports = mongoose.models.LiveClass || mongoose.model('LiveClass', LiveClassSchema);

