}

.section-result-content {
  padding: 20px;
  flex: 1;
  overflow-y: auto;
}

.result-summary {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.result-stats {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
}

.stat-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
  background: #f8f9fa;
}

.stat-label {
  font-weight: 500;
  color: #333;
}

.stat-value {
  font-weight: bold;
  padding: 4px 8px;
  border-radius: 3px;
  color: white;
}

.stat-value.answered {
  background: #28a745;
}

.stat-value.not-answered {
  background: #dc3545;
}

.stat-value.marked {
  background: #ffc107;
  color: #212529;
}

.stat-value.not-visited {
  background: #6c757d;
}

.score-summary {
  background: #e8f0fe;
  padding: 15px;
  border-radius: 6px;
  border: 1px solid #2c5aa0;
}

.score-summary h4 {
  margin: 0 0 10px 0;
  color: #2c5aa0;
}

.score-item {
  margin-bottom: 8px;
  font-size: 14px;
  color: #333;
}

.section-result-actions {
  padding: 20px;
  border-top: 1px solid #ddd;
  text-align: center;
  background: #f8f9fa;
}

/* Final Result Modal */
.final-result-modal {
  background: white;
  border-radius: 8px;
  box-shadow: 0 4px 20px rgba(0,0,0,0.3);
  width: 800px;
  max-height: 90vh;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.final-result-header {
  background: linear-gradient(135deg, #28a745, #20c997);
  color: white;
  padding: 20px;
  text-align: center;
}

.final-result-header h3 {
  margin: 0;
  font-size: 20px;
  font-weight: bold;
}

.final-result-content {
  padding: 20px;
  flex: 1;
  overflow-y: auto;
}

.overall-summary {
  display: flex;
  flex-direction: column;
  gap: 25px;
}

.overall-stats {
  text-align: center;
  background: linear-gradient(135deg, #e8f5e8, #d4edda);
  padding: 20px;
  border-radius: 8px;
  border: 2px solid #28a745;
}

.big-stat {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5px;
}

.big-stat-label {
  font-size: 16px;
  color: #333;
  font-weight: 500;
}

.big-stat-value {
  font-size: 32px;
  font-weight: bold;
  color: #28a745;
}

.big-stat-percentage {
  font-size: 24px;
  font-weight: bold;
  color: #2c5aa0;
}

.section-wise-results h4 {
  margin: 0 0 15px 0;
  color: #333;
  font-size: 16px;
}

.results-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 14px;
}

.results-table th {
  background: #2c5aa0;
  color: white;
  padding: 12px 8px;
  text-align: center;
  font-weight: 500;
  border: 1px solid #1e4080;
}

.results-table td {
  padding: 10px 8px;
  text-align: center;
  border: 1px solid #ddd;
}

.results-table tr:nth-child(even) {
  background: #f8f9fa;
}

.results-table tr:hover {
  background: #e8f0fe;
}

.results-table .section-name {
  font-weight: bold;
  color: #2c5aa0;
  text-align: left;
}

.results-table .answered {
  color: #28a745;
  font-weight: bold;
}

.results-table .not-answered {
  color: #dc3545;
  font-weight: bold;
}

.results-table .marked {
  color: #ffc107;
  font-weight: bold;
}

.results-table .not-visited {
  color: #6c757d;
  font-weight: bold;
}

.final-result-actions {
  padding: 20px;
  border-top: 1px solid #ddd;
  text-align: center;
  background: #f8f9fa;
}

.result-btn {
  padding: 12px 24px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  font-weight: bold;
  transition: all 0.2s;
  min-width: 200px;
}

.result-btn.primary {
  background: #28a745;
  color: white;
}

.result-btn.primary:hover {
  background: #218838;
  transform: translateY(-1px);
}

/* Responsive Design */
@media (max-width: 1200px) {
  .cat-sidebar-panel {
    width: 240px;
  }

  .palette-grid {
    grid-template-columns: repeat(4, 1fr);
  }
}

@media (max-width: 768px) {
  .cat-exam-content {
    flex-direction: column;
  }

  .cat-sidebar-panel {
    width: 100%;
    height: 300px;
    order: -1;
  }

  .cat-question-panel {
    flex: 1;
  }

  .calculator-modal,
  .scratchpad-modal,
  .instructions-modal {
    width: 90%;
    max-width: 400px;
  }

  .scratchpad-modal {
    height: 300px;
  }

  .section-result-modal,
  .final-result-modal {
    width: 95%;
    max-width: 500px;
    max-height: 85vh;
  }

  .result-stats {
    grid-template-columns: 1fr;
  }

  .results-table {
    font-size: 12px;
  }

  .results-table th,
  .results-table td {
    padding: 6px 4px;
  }

  .big-stat-value {
    font-size: 24px;
  }

  .big-stat-percentage {
    font-size: 18px;
  }
}

