body {
  font-family: 'Manrope', sans-serif;
  text-align: left;
}
/* TestimonialsShowcase.css */
.ts-wrapper {
  display: flex;
  justify-content: space-between;
  padding: 60px;
  background: #fff;
  gap: 40px;
  flex-wrap: wrap;
}

.ts-left {
  flex: 0 0 55%;
