  text-transform: uppercase;
  color:#EE6A4D;
}

.ts-title {
  font-family: DM Serif Display;
  font-weight: 400;
  font-size: 48px;
  line-height: 120%;
  letter-spacing: 0%;
  text-transform: capitalize;
  color:#1A237E;
}

.ts-desc {
  position: relative;
  top: -20px;
  font-family: 'Manrope',sans-serif;
  font-weight: 500;
  font-size: 20px;
  line-height: 160%;
  letter-spacing: 0%;
  color: #000000;
}

.ts-btn {
   position: relative;
  top: -20px;
  background: #ee6a4d;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 30px;
}

.ts-countering {
   position: relative;
  top: -20px;
  display: flex;

  gap: 40px;
  font-size: 54px;
  margin-bottom: 30px;
}

.ts-countering h3 {
   /* position: relative;
  top: -20px; */
  font-size: 58px;
  font-family: "Manrope",sans-serif;
  gap: 70px;
  color: #9747FF;
  margin-bottom: 30px;
  font-weight: 700;
}

.ts-countering p {
   position: relative;
  top: -15px;
  left: 10px;
  font-family: 'Manrope', sans-serif;
  font-weight: 500;
  font-size: 16px;
  line-height: 120%;
  letter-spacing: 0%;
  text-align: center;
  text-transform: capitalize;
}

.ts-awards {
   position: relative;
  top: -40px;
  background: #1A237E;
  color: white;
  padding: 24px;
  border-radius: 20px;
  margin-top: 30px;
  font-family: 'Manrope', sans-serif;
}

.ts-award-title {
  color: #FFC107;
  font-size: 20px;
  font-weight: 600;
  margin-bottom: 16px;
}

.ts-award-flex-line {
  display: flex;
  align-items: flex-start;
  gap: 20px;
  margin-bottom: 24px;
  flex-wrap: wrap;
}

.ts-award-col {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.ts-year {
  color: #EE6A4D;
  font-size: 40px;
  font-weight: 700;
  min-width: 70px;
}

.ts-tag {
  color: white;
  font-size: 16px;
  font-weight: 600;
  text-align: left;
  min-width: 80px;
}

.ts-rank {
  color: #EE6A4D;
  font-size: 32px;
  font-weight: 700;
  display: flex;
  align-items: flex-start;
}

.ts-rank sup {
  font-size: 16px;
  vertical-align: super;
  margin-left: 2px;
  line-height: 1;
}

.ts-summary {
  font-family: 'Manrope', sans-serif;
  font-weight: 600;
  font-size: 14px;
  line-height: 160%;
  letter-spacing: 0%;
  flex: 1;
}

.ts-award-ranks-boxes {
  display: flex;
  gap: 12px;
  justify-content: space-between;
}

.ts-award-ranks-boxes p {
  background: #121850;
  font-family: 'Manrope', sans-serif;
  padding: 8px 16px;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 500;
  margin: 0;
  flex: 1 1 250px;
}

.ts-right {
  flex: 0 0 40%;
  max-width: 41%;
  max-height: 800px;
  padding-right: 6px;
  overflow-y: auto;
  scroll-behavior: smooth;
}

.ts-testimonial-scroll {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 10px;
  height: 200px;
  animation: scroll-infinite 40s linear infinite;
   align-items: stretch;  
}

@keyframes scroll-infinite {
  0% { transform: translateY(0); }
  100% { transform: translateY(-50%); }
}

.ts-testimonial-image-card {
  width: 100%;
  height: 200px;
  overflow: hidden;
  border-radius: 8px;
    line-height: 0;  
}

.ts-testimonial-full-image {
  width: 100%;
  height: 100%;

  display: block;
   object-fit: contain; 
}


@media (max-width: 768px){
  .ts-testimonial-scroll{ height: 160px; }
  .ts-testimonial-image-card{ height: 160px; }
}


.tgL-scroll-wrapper {
  display: flex;
  flex-wrap: nowrap;         /* Important: sab ek hi line me */
  gap: 10px;                 /* gap between cards */
  overflow-x: auto;          /* horizontal scroll */
  padding: 10px;             
  scrollbar-width: thin;     /* Firefox scrollbar */
}

.tgL-scroll-wrapper::-webkit-scrollbar {
  height: 8px;
}

.tgL-scroll-wrapper::-webkit-scrollbar-thumb {
  background: #999;
  border-radius: 4px;
}

.tgL-scroll-wrapper::-webkit-scrollbar-track {
  background: #eee;
}

.tgL-card {
  flex: 0 0 auto;            /* Prevent shrinking, fixed width */
  width: 300px;              /* Set width of each card */
  border-radius: 8px;
  background: #fff;
  overflow: hidden;
  box-shadow: 0 0 8px rgba(0,0,0,0.1); /* Optional: soft shadow */
}

.tgL-image {
  width: 100%;
  height: auto;
  display: block;
}

