              </label>
            </div>
            <div className="preference-item">
              <span>SMS Reminders</span>
              <label className="toggle">
                <input type="checkbox" defaultChecked />
                <span className="slider"></span>
              </label>
            </div>
            <div className="preference-item">
              <span>Performance Analytics</span>
              <label className="toggle">
                <input type="checkbox" defaultChecked />
                <span className="slider"></span>
              </label>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'dashboard': return renderDashboardContent();
      case 'courses': return renderCoursesContent();
      case 'my-courses': return renderMyCoursesContent();
      case 'live-classes': return renderLiveClassesContent();
      case 'practice-tests': return renderPracticeTestsContent();
      case 'mock-tests': return renderMockTestsContent();
      case 'analysis': return renderAnalysisContent();
      case 'doubts': return renderDoubtsContent();
      case 'materials': return renderMaterialsContent();
      case 'schedule': return renderScheduleContent();
      case 'announcements': return renderAnnouncementsContent();
      case 'purchases': return renderPurchasesContent();
      case 'profile': return renderProfileContent();
      default: return renderDashboardContent();
    }
  };

  return (
    <div className="student-lms">
      {/* Sidebar */}
      <div className={`lms-sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
    <div className="logo">
      {/* â¬‡ï¸ yeh naya image element */}
      <img src={logo} alt="TathaGat LMS" className="logo-img" />
      {/* agar text nahi chahiye to <h2> hata do; rakhna ho to isse rehne do */}
      {/* <h2>TathaGat LMS</h2> */}
    </div>
    <button className="close-sidebar" onClick={() => setSidebarOpen(false)}>
      <FiX />
    </button>
  </div>

        <nav className="sidebar-nav">
          {sidebarItems.map((item) => (
            <button
              key={item.id}
              className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
              onClick={() => {
                setActiveSection(item.id);
                setSidebarOpen(false);
              }}
            >
              <item.icon className="nav-icon" />
              <span>{item.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="lms-main">
        {/* Top Navigation */}
        <header className="lms-header">
          <div className="header-left">
            <button className="menu-toggle" onClick={() => setSidebarOpen(true)}>
              <FiMenu />
            </button>
            <div className="search-box">
              <FiSearch className="search-icon" />
              <input type="text" placeholder="Search courses, materials..." />
            </div>
          </div>

          <div className="header-right">
            <button className="notification-btn">
              <FiBell />
              <span className="notification-badge">3</span>
            </button>

            <div className="profile-dropdown">
              <button
                className="profile-btn"
                onClick={() => setProfileDropdownOpen(!profileDropdownOpen)}
              >
                <div className="profile-avatar">
                  <FiUser />
                </div>
                <span>{userDetails.name.split(' ')[0]}</span>
                <FiChevronDown />
              </button>

              {profileDropdownOpen && (
                <div className="dropdown-menu">
                  <button onClick={() => setActiveSection('profile')}>
                    <FiUser /> Profile
                  </button>
                  <button>
                    <FiBell /> Notifications
                  </button>
                  <hr />
                  <button className="logout-btn">
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </header>

        {/* Content Area */}
        <main className="lms-content">
          {renderContent()}
        </main>
      </div>

      {/* Preview Modal */}
      {previewOpen && (
        <div className="preview-overlay" role="dialog" aria-modal="true" onClick={closePreview}>
          <div className="preview-sheet" onClick={(e) => e.stopPropagation()}>
            <div className="preview-header">
              <h3 className="preview-title">Course Overview</h3>
              <button className="preview-close" onClick={closePreview} aria-label="Close">
                <FiX />
              </button>
            </div>
            <div className="preview-body" ref={previewRef}>
              {previewLoading ? (
                <div className="preview-loading">Loading...</div>
              ) : (
                <div className="overview-content">
                  <h4 className="ov-title">{previewData?.name}</h4>
                  <p className="ov-desc">{previewData?.overview?.description || previewData?.description || 'No description available.'}</p>
                  <div className="ov-section">
                    <h5>Material Includes</h5>
                    {Array.isArray(previewData?.overview?.materialIncludes) && previewData.overview.materialIncludes.length ? (
                      <ul className="ov-list">
                        {previewData.overview.materialIncludes.map((it, idx) => (
                          <li key={idx}>{it}</li>
                        ))}
                      </ul>
                    ) : (
                      <p className="ov-muted">No materials listed.</p>
                    )}
                  </div>
                  <div className="ov-section">
                    <h5>Requirements</h5>
                    {Array.isArray(previewData?.overview?.requirements) && previewData.overview.requirements.length ? (
                      <ul className="ov-list">
                        {previewData.overview.requirements.map((it, idx) => (
                          <li key={idx}>{it}</li>
                        ))}
                      </ul>
                    ) : (
                      <p className="ov-muted">No requirements specified.</p>
                    )}
                  </div>
                </div>
              )}
            </div>
            <div className="preview-actions">
              <button className="download-btn" onClick={downloadOverviewPdf} disabled={previewLoading}>
                <FiDownload /> Download
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Material Viewer Modal */}
      {materialViewerOpen && (
        <div className="material-viewer-overlay" role="dialog" aria-modal="true" onClick={closeMaterialViewer}>
          <div className="material-viewer-sheet" onClick={(e) => e.stopPropagation()}>
            <div className="material-viewer-header">
              <h3 className="material-viewer-title">{selectedMaterial?.title}</h3>
              <button className="material-viewer-close" onClick={closeMaterialViewer} aria-label="Close">
                <FiX />
              </button>
            </div>
            <div className="material-viewer-body">
              {materialViewerLoading ? (
                <div className="material-viewer-loading">Loading material...</div>
              ) : materialPdfUrl ? (
                <div className="material-viewer-content">
                  <div className="material-details">
                    <div className="detail-section">
                      <label>Type:</label>
                      <span>{selectedMaterial?.type}</span>
                    </div>
                    <div className="detail-section">
                      <label>Subject:</label>
                      <span>{selectedMaterial?.subject}</span>
                    </div>
                    <div className="detail-section">
                      <label>File Size:</label>
                      <span>{selectedMaterial?.fileSize}</span>
                    </div>
                    <div className="detail-section">
                      <label>Downloads:</label>
                      <span>{selectedMaterial?.downloadCount}</span>
                    </div>
                    {selectedMaterial?.description && (
                      <div className="detail-section full-width">
                        <label>Description:</label>
                        <p>{selectedMaterial.description}</p>
                      </div>
                    )}
                  </div>
                  <div className="material-pdf-container">
                    <iframe
                      src={materialPdfUrl}
                      title="Material Preview"
                      className="material-pdf-viewer"
                    />
                  </div>
                </div>
              ) : (
                <div className="material-viewer-error">Failed to load material. Please try again.</div>
              )}
            </div>
            <div className="material-viewer-actions">
              <button
                className="download-btn"
                onClick={async () => {
                  const authToken = localStorage.getItem('authToken');
                  try {
                    const response = await fetch(`/api/study-materials/download/${selectedMaterial._id}`, {
                      headers: {
                        'Authorization': `Bearer ${authToken}`
                      }
                    });
                    if (response.ok) {
                      const blob = await response.blob();
                      const url = window.URL.createObjectURL(blob);
                      const link = document.createElement('a');
                      link.href = url;
                      link.download = selectedMaterial?.title || 'material.pdf';
                      document.body.appendChild(link);
                      link.click();
                      document.body.removeChild(link);
                      window.URL.revokeObjectURL(url);
                    }
                  } catch (error) {
                    console.error('Download error:', error);
                    alert('Failed to download file');
                  }
                }}
              >
                <FiDownload /> Download
              </button>
              <button className="close-btn" onClick={closeMaterialViewer}>
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Mobile Overlay */}
      {sidebarOpen && <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}></div>}
    </div>
  );
};

export default StudentDashboard;

backend1/models/DiscussionReply.js.content:
const mongoose = require('mongoose');

const discussionReplySchema = new mongoose.Schema({
  content: {
    type: String,
    required: [true, 'Reply content is required'],
    trim: true
  },
  discussionId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Discussion',
    required: true
  },
  repliedBy: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  parentReply: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'DiscussionReply',
    default: null
  },
  status: {
    type: String,
    enum: ['pending', 'approved', 'rejected'],
    default: 'pending'
  },
  isPublished: {
    type: Boolean,
    default: false
  },
  isBestAnswer: {
    type: Boolean,
    default: false
  },
  upvotes: [{
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User'
  }],
  downvotes: [{
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User'
  }],
  moderatedBy: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Admin'
  },
  moderationNote: {
    type: String,
    trim: true
  },
  moderatedAt: {
    type: Date
  },
  editedAt: {
    type: Date
  },
  isEdited: {
    type: Boolean,
    default: false
  }
}, {
  timestamps: true
});

// Index for better query performance
discussionReplySchema.index({ discussionId: 1, createdAt: 1 });
discussionReplySchema.index({ repliedBy: 1, createdAt: -1 });
discussionReplySchema.index({ status: 1, isPublished: 1 });
discussionReplySchema.index({ parentReply: 1 });

// Virtual for upvote count
discussionReplySchema.virtual('upvoteCount').get(function() {
  return this.upvotes.length;
});

// Virtual for downvote count
discussionReplySchema.virtual('downvoteCount').get(function() {
  return this.downvotes.length;
});

module.exports = mongoose.model('DiscussionReply', discussionReplySchema);

Frontend1/src/pages/mainAdmin/StudyMater.content:
import React, { useState, useEffect } from 'react';
import { FaPlus, FaDownload, FaEdit, FaTrash, FaEye, FaFileAlt, FaFilter, FaSearch } from 'react-icons/fa';
import { toast } from 'react-toastify';
import './StudyMaterials.css';

const StudyMaterials = () => {
  const [materials, setMaterials] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [filters, setFilters] = useState({
    subject: 'All Subjects',
    type: 'All Types',
    search: ''
  });
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    pages: 0
  });

  const [uploadData, setUploadData] = useState({
    title: '',
    description: '',
    subject: 'Quantitative Aptitude',
    type: 'PDF',
    tags: '',
    file: null
  });

  const [uploadLoading, setUploadLoading] = useState(false);

  const subjects = ['All Subjects', 'Quantitative Aptitude', 'Verbal Ability', 'Data Interpretation', 'Logical Reasoning', 'General Knowledge'];
  const types = ['All Types', 'PDF', 'Video', 'Practice Sets', 'Notes', 'Other'];

  // Fetch study materials
  const fetchMaterials = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('authToken');
      const queryParams = new URLSearchParams({
        page: pagination.page,
        limit: pagination.limit,
        ...(filters.subject !== 'All Subjects' && { subject: filters.subject }),
        ...(filters.type !== 'All Types' && { type: filters.type }),
        ...(filters.search && { search: filters.search })
      });

      const response = await fetch(`/api/study-materials/admin?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();

      if (data.success) {
        setMaterials(data.data);
        setPagination(data.pagination);
      } else {
        toast.error(data.message || 'Failed to fetch study materials');
      }
    } catch (error) {
      console.error('Error fetching materials:', error);
      toast.error('Failed to fetch study materials');
    } finally {
      setLoading(false);
    }
  };

  // Upload study material
  const handleUpload = async (e) => {
    e.preventDefault();

    if (!uploadData.file) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploadLoading(true);

    try {
      const token = localStorage.getItem('authToken');
      const formData = new FormData();

      formData.append('title', uploadData.title);
      formData.append('description', uploadData.description);
      formData.append('subject', uploadData.subject);
      formData.append('type', uploadData.type);
      formData.append('tags', uploadData.tags);
      formData.append('file', uploadData.file);

      const response = await fetch('/api/study-materials/admin/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        toast.success('Study material uploaded successfully!');
        setShowUploadModal(false);
        setUploadData({
          title: '',
          description: '',
          subject: 'Quantitative Aptitude',
          type: 'PDF',
          tags: '',
          file: null
        });
        fetchMaterials();
      } else {
        toast.error(data.message || 'Failed to upload study material');
      }
    } catch (error) {
      console.error('Error uploading material:', error);
      toast.error('Failed to upload study material');
    } finally {
      setUploadLoading(false);
    }
  };

  // Delete study material
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this study material?')) {
      return;
    }

    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch(`/api/study-materials/admin/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();

      if (data.success) {
        toast.success('Study material deleted successfully!');
        fetchMaterials();
      } else {
        toast.error(data.message || 'Failed to delete study material');
      }
    } catch (error) {
      console.error('Error deleting material:', error);
      toast.error('Failed to delete study material');
    }
  };

  // Handle filter changes
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  // Handle pagination
  const handlePageChange = (newPage) => {
    setPagination(prev => ({ ...prev, page: newPage }));
  };

  useEffect(() => {
    fetchMaterials();
  }, [pagination.page, filters]);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getFileIcon = (type) => {
    switch (type) {
      case 'PDF': return 'ðŸ“„';
      case 'Video': return 'ðŸŽ¥';
      case 'Practice Sets': return 'ðŸ“';
      case 'Notes': return 'ðŸ“–';
      default: return 'ðŸ“';
    }
  };

  return (
    <div className="study-materials-container">
      <div className="study-materials-header">
        <div className="header-left">
          <h1><FaFileAlt /> Study Materials Management</h1>
          <p>Upload and manage study materials for students</p>
        </div>
        <button 
          className="upload-btn"
          onClick={() => setShowUploadModal(true)}
        >
          <FaPlus /> Upload Material
        </button>
      </div>

      {/* Filters */}
      <div className="materials-filters">
        <div className="filter-group">
          <FaFilter className="filter-icon" />
          <select 
            value={filters.subject}
            onChange={(e) => handleFilterChange('subject', e.target.value)}
          >
            {subjects.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <select 
            value={filters.type}
            onChange={(e) => handleFilterChange('type', e.target.value)}
          >
            {types.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>

        <div className="search-group">
          <FaSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search materials..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
          />
        </div>
      </div>

      {/* Materials Table */}
      <div className="materials-table-container">
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading study materials...</p>
          </div>
        ) : (
          <table className="materials-table">
            <thead>
              <tr>
                <th>Material</th>
                <th>Subject</th>
                <th>Type</th>
                <th>Size</th>
                <th>Downloads</th>
                <th>Uploaded</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {materials.length === 0 ? (
                <tr>
                  <td colSpan="7" className="no-data">
                    No study materials found
                  </td>
                </tr>
              ) : (
                materials.map((material) => (
                  <tr key={material._id}>
                    <td className="material-info">
                      <div className="material-details">
                        <span className="file-icon">{getFileIcon(material.type)}</span>
                        <div>
                          <h4>{material.title}</h4>
                          <p>{material.description}</p>
                        </div>
                      </div>
                    </td>
                    <td>
                      <span className="subject-badge">{material.subject}</span>
                    </td>
                    <td>
                      <span className="type-badge">{material.type}</span>
                    </td>
                    <td>{material.fileSize}</td>
                    <td>{material.downloadCount}</td>
                    <td>{formatDate(material.createdAt)}</td>
                    <td className="actions">
                      <button 
                        className="action-btn view-btn"
                        title="View Details"
                      >
                        <FaEye />
                      </button>
                      <button 
                        className="action-btn edit-btn"
                        title="Edit Material"
                      >
                        <FaEdit />
                      </button>
                      <button 
                        className="action-btn delete-btn"
                        title="Delete Material"
                        onClick={() => handleDelete(material._id)}
                      >
                        <FaTrash />
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Pagination */}
      {pagination.pages > 1 && (
        <div className="pagination">
          <button 
            disabled={pagination.page === 1}
            onClick={() => handlePageChange(pagination.page - 1)}
          >
            Previous
          </button>

          <span className="page-info">
            Page {pagination.page} of {pagination.pages}
          </span>

          <button 
            disabled={pagination.page === pagination.pages}
            onClick={() => handlePageChange(pagination.page + 1)}
          >
            Next
          </button>
        </div>
      )}

      {/* Upload Modal */}
      {showUploadModal && (
        <div className="modal-overlay">
          <div className="upload-modal">
            <div className="modal-header">
              <h2>Upload Study Material</h2>
              <button 
                className="close-btn"
                onClick={() => setShowUploadModal(false)}
              >
                Ã—
              </button>
            </div>

            <form onSubmit={handleUpload} className="upload-form">
              <div className="form-group">
                <label>Title *</label>
                <input
                  type="text"
                  value={uploadData.title}
                  onChange={(e) => setUploadData(prev => ({ ...prev, title: e.target.value }))}
                  required
                  placeholder="Enter material title"
                />
              </div>

              <div className="form-group">
                <label>Description</label>
                <textarea
                  value={uploadData.description}
                  onChange={(e) => setUploadData(prev => ({ ...prev, description: e.target.value }))}
                  placeholder="Enter description (optional)"
                  rows="3"
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Subject *</label>
                  <select
                    value={uploadData.subject}
                    onChange={(e) => setUploadData(prev => ({ ...prev, subject: e.target.value }))}
                    required
                  >
                    {subjects.filter(s => s !== 'All Subjects').map(subject => (
                      <option key={subject} value={subject}>{subject}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Type *</label>
                  <select
                    value={uploadData.type}
                    onChange={(e) => setUploadData(prev => ({ ...prev, type: e.target.value }))}
                    required
                  >
                    {types.filter(t => t !== 'All Types').map(type => (
                      <option key={type} value={type}>{type}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Tags</label>
                <input
                  type="text"
                  value={uploadData.tags}
                  onChange={(e) => setUploadData(prev => ({ ...prev, tags: e.target.value }))}
                  placeholder="Enter tags separated by commas"
                />
              </div>

              <div className="form-group">
                <label>File *</label>
                <input
                  type="file"
                  onChange={(e) => setUploadData(prev => ({ ...prev, file: e.target.files[0] }))}
                  accept=".pdf,.doc,.docx,.jpg,.jpeg,.png,.mp4,.avi,.mov,.wmv"
                  required
                />
                <small>Supported formats: PDF, Word documents, Images, Videos (Max: 100MB)</small>
              </div>

              <div className="form-actions">
                <button 
                  type="button"
                  className="cancel-btn"
                  onClick={() => setShowUploadModal(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit"
                  className="submit-btn"
                  disabled={uploadLoading}
                >
                  {uploadLoading ? 'Uploading...' : 'Upload Material'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudyMaterials;

