              <button className={`tgcp-tab-btn ${activeTab === "curriculum" ? "active" : ""}`} onClick={() => handleTabClick("curriculum")}>ðŸ“„ Curriculum</button>
              <button className={`tgcp-tab-btn ${activeTab === "instructor" ? "active" : ""}`} onClick={() => handleTabClick("instructor")}>ðŸ‘¤ Instructors</button>
              <button className={`tgcp-tab-btn ${activeTab === "reviews" ? "active" : ""}`} onClick={() => handleTabClick("reviews")}>â­ Reviews</button>
            </div>

