@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap');

body {
  margin: 0;
  font-family: 'Poppins', sans-serif;
  background: #ffffff;
  color: #ffffff;
  text-align: center;
  overflow-x: hidden;

}

.unique-main-container {
  max-width: 1400px;
  margin: auto;
  margin-top: 50px;
}

.unique-content-wrapper {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  flex-wrap: nowrap;
}

/* Left and Right Section */
.unique-left-section {
  width: 60%;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.unique-right-section {
  width: 40%;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-left: 40px;
}

/* Banner Section */
.unique-banner-section,
.unique-details-section,
.unique-feedback-section {
  background: #fff;
  border-radius: 12px;
  border: solid #dc0000;
  padding: 30px;
  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.2);
  color: #dc0000;
  text-align: center;
  width: 1030px;
  margin-bottom: 20px;
}
.unique-banner-buttons .unique-primary-button,.unique-secondary-button{
  color: white;
  background: #dc0000;
  padding: 10px 20px;
  margin: 0 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  box-shadow: 0 2px 10px rgb(0 0 0 / 52%);
  font-size: 11px;
}
.unique-feedback-section h2{
  color:#dc0000;
}

/* Feedback Slider (Original Animation Restore) */
.unique-feedback-slider {
