  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  width: 250px;
  text-align: center;
  transition: all 0.3s ease-in-out;
}

.unique-feature-boxs:hover {
