import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminLayout from "../AdminLayout/AdminLayout";
import "./CourseContentManager.css";
import { FaEdit, FaTrash } from "react-icons/fa";
import ChapterTab from "./ChapterTab/ChapterTab";
import TopicTab from "./TopicTab/TopicTab";
import AddTest from "./AddTest/AddTest";
import AddQuestion from "./AddQuestion/AddQuestion"


const CourseContentManager = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourseId, setSelectedCourseId] = useState("");
  const [subjects, setSubjects] = useState([]);
  const [subjectName, setSubjectName] = useState("");
  const [editingSubject, setEditingSubject] = useState(null);
  const [editSubjectName, setEditSubjectName] = useState("");
  const [activeTab, setActiveTab] = useState("subject");

  // âœ… Fetch all courses for dropdown
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const token = localStorage.getItem("adminToken");
        const res = await axios.get("/api/courses", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCourses(res.data.courses || []);
      } catch (error) {
        console.error("Error fetching courses", error);
      }
    };
    fetchCourses();
  }, []);

  // âœ… Fetch subjects when course selected
useEffect(() => {
  if (!selectedCourseId) return;
  const fetchSubjects = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.get(
        `/api/subjects/${selectedCourseId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSubjects(res.data.subjects || []);
    } catch (error) {
      console.error("Error fetching subjects", error);
    }
  };
  fetchSubjects();
}, [selectedCourseId]);


  // âœ… Handle subject add
  const handleAddSubject = async () => {
    if (!subjectName || !selectedCourseId) return alert("Fill all fields!");
    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.post(
        "/api/subjects",
        {
          name: subjectName,
          courseId: selectedCourseId,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("âœ… Subject added!");
      console.log("Sending:", {
        courseId: selectedCourseId,
        name: subjectName,
      });

      setSubjectName("");
      // Force re-fetch all subjects for the selected course
      //   const refetch = async () => {
      //     const token = localStorage.getItem("adminToken");
      //     const res = await axios.get(
      //       `/api/subjects/${selectedCourseId}`,
      //       {
      //         headers: { Authorization: `Bearer ${token}` },
      //       }
      //     );
      //     setSubjects(res.data.subjects || []);
      //   };
      //   refetch();
      fetchSubjects();
    } catch (error) {
      console.error("Add subject failed", error);
      alert("Error adding subject");
    }
  };

  const fetchSubjects = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.get(
        `/api/subjects/${selectedCourseId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSubjects(res.data.subjects || []);
    } catch (error) {
      console.error("Error fetching subjects", error);
    }
  };

  const handleDeleteSubject = async (id) => {
    if (!window.confirm("Are you sure you want to delete this subject?"))
      return;
    try {
      const token = localStorage.getItem("adminToken");
      await axios.delete(`/api/subjects/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Subject deleted âœ…");
      fetchSubjects();
    } catch (err) {
      alert("Delete failed");
      console.error(err);
    }
  };

  return (
    <AdminLayout>
      <div className="content-manager">
        <h2 className="page-title">ðŸ“š Course Content Manager</h2>

        {/* ðŸ”¥ Tabs for Subject & Chapter */}
        <div className="tab-buttons">
          <button
            className={activeTab === "subject" ? "active-tab" : ""}
            onClick={() => setActiveTab("subject")}
          >
            Subjects
          </button>
          <button
            className={activeTab === "chapter" ? "active-tab" : ""}
            onClick={() => setActiveTab("chapter")}
          >
            Chapters
          </button>
          <button
  className={activeTab === "topic" ? "active-tab" : ""}
  onClick={() => setActiveTab("topic")}
>
  Topics
</button>
<button
  className={activeTab === "test" ? "active-tab" : ""}
  onClick={() => setActiveTab("test")}
>
  Tests
</button>
<button
  className={activeTab === "question" ? "active-tab" : ""}
  onClick={() => setActiveTab("question")}
>
  Questions
</button>


        </div>

        {/* Course Dropdown */}
        <div className="form-group">
          <label>Select Course</label>
          <select
            value={selectedCourseId}
            onChange={(e) => setSelectedCourseId(e.target.value)}
          >
            <option value="">-- Select Course --</option>
            {courses.map((course) => (
              <option key={course._id} value={course._id}>
                {course.name}
              </option>
            ))}
          </select>
        </div>

        {/* Add Subject Form */}
        {selectedCourseId && activeTab === "subject" && (
          <>
            <div className="form-group">
              <label>New Subject Name</label>
              <input
                type="text"
                value={subjectName}
                onChange={(e) => setSubjectName(e.target.value)}
                placeholder="Enter subject name"
              />
              <button className="add-btn" onClick={handleAddSubject}>
                âž• Add Subject
              </button>
            </div>

            {/* Subjects List */}
            <div className="table-wrapper">
              <h3>ðŸ“– Subjects List</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sr.no</th>
                    <th>Subject Name</th>
                    <th>Created</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {subjects.map((sub, idx) => (
                    <tr key={sub._id}>
                      <td>{idx + 1}</td>
                      <td>{sub.name}</td>
                      <td>{new Date(sub.createdAt).toLocaleDateString()}</td>
                      <td className="icon-actions">
                        <FaEdit
                          title="Edit"
                          style={{
                            cursor: "pointer",
                            marginRight: "10px",
                            color: "#007bff",
                          }}
                          onClick={() => {
                            setEditingSubject(sub);
                            setEditSubjectName(sub.name);
                          }}
                        />
                        <FaTrash
                          title="Delete"
                          style={{ cursor: "pointer", color: "red" }}
                          onClick={() => handleDeleteSubject(sub._id)}
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}

        {selectedCourseId && activeTab === "chapter" && (
          <ChapterTab selectedCourseId={selectedCourseId} />
        )}

        {selectedCourseId && activeTab === "topic" && (
        <TopicTab />
         )}
{selectedCourseId && activeTab === "test" && (
  <AddTest selectedCourseId={selectedCourseId} subjects={subjects} />
)}

{selectedCourseId && activeTab === "question" && (
  <AddQuestion selectedCourseId={selectedCourseId} subjects={subjects} />
)}



      </div>
      {editingSubject && (
        <div className="edit-modal">
          <div className="edit-modal-content">
            <h3>Edit Subject</h3>
            <input
              type="text"
              value={editSubjectName}
              onChange={(e) => setEditSubjectName(e.target.value)}
            />
            <button
              onClick={async () => {
                try {
                  const token = localStorage.getItem("adminToken");
                  await axios.put(
                    `/api/subjects/${editingSubject._id}`,
                    {
                      name: editSubjectName,
                    },
                    {
                      headers: { Authorization: `Bearer ${token}` },
                    }
                  );
                  alert("Subject updated âœ…");
                  setEditingSubject(null);
                  fetchSubjects(); 
                } catch (err) {
                  console.error("Edit failed", err);
                  alert("Update failed");
                }
              }}
            >
              Update
            </button>
            <button onClick={() => setEditingSubject(null)}>Cancel</button>
          </div>
        </div>
      )}
    </AdminLayout>
  );
};

export default CourseContentManager;

backend1/utils/SendOtp.js.content:
const nodemailer = require('nodemailer');
require('dotenv').config();
const axios=require("axios")

// Create transporter with better error handling
const createTransporter = async () => {
    // For development, use a demo email service
    if (process.env.NODE_ENV === 'development') {
        try {
            // Create a test account for development
            const testAccount = await nodemailer.createTestAccount();

            console.log('ðŸ“§ Using Ethereal Email for development testing');
            console.log('Test account:', testAccount.user);

            return nodemailer.createTransport({
                host: 'smtp.ethereal.email',
                port: 587,
                secure: false,
                auth: {
                    user: testAccount.user,
                    pass: testAccount.pass,
                },
            });
        } catch (error) {
            console.warn('âš ï¸ Could not create test email account, falling back to Gmail');
        }
    }

    // Production Gmail configuration
    if (!process.env.EMAIL || !process.env.EMAIL_PASSWORD) {
        console.warn('âš ï¸ Gmail credentials not configured');
        return null;
    }

    return nodemailer.createTransport({
        service: 'gmail',
        auth: {
            user: process.env.EMAIL,
            pass: process.env.EMAIL_PASSWORD,
        },
    });
};

let transporter = null;
createTransporter().then(t => { transporter = t; });

exports.sendOtpEmailUtil = async (email, otpCode) => {
    try {
        // Wait for transporter to be initialized if needed
        if (!transporter) {
            transporter = await createTransporter();
        }

        if (!transporter) {
            throw new Error('Email transporter not configured');
        }

        const mailOptions = {
            from: process.env.EMAIL || 'noreply@tathagat.com',
            to: email,
            subject: 'ðŸ” TathaGat - Your OTP Code',
            html: `
                <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
                    <h2 style="color: #4f46e5; text-align: center;">TathaGat OTP Verification</h2>
                    <div style="background: #f8fafc; padding: 20px; border-radius: 8px; text-align: center;">
                        <p style="font-size: 18px; margin: 10px 0;">Your OTP code is:</p>
                        <h1 style="font-size: 32px; color: #1a202c; letter-spacing: 4px; margin: 20px 0;">${otpCode}</h1>
                        <p style="color: #718096; font-size: 14px;">This code will expire in 5 minutes.</p>
                    </div>
                    <p style="color: #4a5568; text-align: center; margin-top: 20px;">
                        If you didn't request this code, please ignore this email.
                    </p>
                </div>
            `,
            text: `Your TathaGat OTP code is: ${otpCode}. It will expire in 5 minutes.`,
        };

        const info = await transporter.sendMail(mailOptions);

        // For development with Ethereal, log the preview URL
        if (process.env.NODE_ENV === 'development' && info.messageId) {
            const previewUrl = nodemailer.getTestMessageUrl(info);
            if (previewUrl) {
                console.log('ðŸ“§ Preview email: ' + previewUrl);
            }
        }

        console.log(`âœ… Email sent successfully to ${email} (Message ID: ${info.messageId})`);

    } catch (error) {
        console.error('âŒ Error sending OTP email:', error.message);
        throw error; // Re-throw so controller can handle it
    }
};


exports.sendOtpPhoneUtil = async (phoneNumber, otpCode) => {
    try {
      const payload = {
        api_key: process.env.KARIX_API_KEY, // Karix API Key from .env
        to: phoneNumber,
        sender: process.env.KARIX_SENDER_ID, // Karix Sender ID
        message: `Your OTP is ${otpCode}`
      };

      const response = await axios.post("https://alerts.karix.co/api/v1/message", payload, {
        headers: { "Content-Type": "application/json" }
      });


      console.log("âœ… OTP Sent Successfully!", response.data);
    } catch (error) {
      console.error("âŒ Error sending OTP:", error.response ? error.response.data : error);
    }
  };

Frontend1/src/pages/MainSubAdmin/LiveCla.content:
import React, { useEffect, useMemo, useState } from 'react';
import { toast } from 'react-toastify';
import Filters from '../../../components/LiveClasses/Filters';
import TableView from '../../../components/LiveClasses/TableView';
import CalendarView from '../../../components/LiveClasses/CalendarView';
import LiveClassForm from '../../../components/LiveClasses/LiveClassForm';
import '../../../components/LiveClasses/liveClasses.css';
import { fetchLiveClasses, createLiveClass, updateLiveClass } from '../../../utils/liveClassesApi';
import { getCache, setCache, shouldRevalidate } from '../../../utils/liveClassesCache';
import axios from '../../../utils/axiosConfig';

const scope = 'teacher';

const TeacherLiveClasses = () => {
  const [tab, setTab] = useState('table');
  const [filters, setFilters] = useState({});
  const [items, setItems] = useState([]);
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editItem, setEditItem] = useState(null);

  useEffect(()=>{ loadCourses(); hydrate(); }, []);

  const hydrate = async () => {
    const cached = getCache(scope);
    setItems(cached.items || []);
    setFilters(cached.filters || {});
    if (shouldRevalidate(scope)) {
      await refresh();
    }
  };

  const [offline, setOffline] = useState(false);
  const refresh = async () => {
    try {
      const data = await fetchLiveClasses({ ...filters, role: 'teacher' });
      setItems(data);
      setCache(scope, data, filters);
      setOffline(false);
    } catch (e) {
      setOffline(true);
      const cached = getCache(scope).items || [];
      setItems(cached);
    }
  };

  const loadCourses = async () => {
    try {
      const res = await axios.get('/api/courses');
      setCourses((res.data?.courses || res.data || []).map(c => ({ _id: c._id, name: c.name })));
    } catch {}
  };

  const startCreate = () => { setEditItem(null); setShowForm(true); };
  const startEdit = (it) => { setEditItem(it); setShowForm(true); };

  const onSubmit = async (payload) => {
    try {
      if (editItem) {
        await updateLiveClass(editItem._id, payload);
        toast.success('Updated');
      } else {
        const created = await createLiveClass(payload);
        try { sessionStorage.setItem('live:classes:lastCreated:v1', JSON.stringify(created)); } catch {}
        toast.success('Created');
      }
      setShowForm(false); setEditItem(null);
      await refresh();
    } catch (e) {
      toast.error(e?.response?.data?.message || 'Save failed');
    }
  };

  const filteredItems = useMemo(()=> items, [items]);

  return (
    <div className="lc-container">
      <div className="lc-header">
        <div className="lc-tabs">
          <button className={`lc-tab ${tab==='table'?'active':''}`} onClick={()=>setTab('table')}>Table</button>
          <button className={`lc-tab ${tab==='calendar'?'active':''}`} onClick={()=>setTab('calendar')}>Calendar</button>
        </div>
        <div className="lc-actions">
          <button className="lc-btn" onClick={refresh}>Refresh</button>
          <button className="lc-btn primary" onClick={startCreate}>Create</button>
        </div>
      </div>

      {offline && <div className="lc-banner">Offline/cache view</div>}

      <Filters courses={courses} filters={filters} onChange={setFilters} onSearch={refresh} />

      <div style={{display: tab==='table' ? 'block' : 'none'}}>
        <TableView items={filteredItems} onEdit={startEdit} />
      </div>
      <div style={{display: tab==='calendar' ? 'block' : 'none'}}>
        <CalendarView items={filteredItems} />
      </div>

      <div className="lc-stat-card lc-section" style={{display: showForm ? 'block' : 'none'}}>
        <LiveClassForm courses={courses} value={editItem} onCancel={()=>{ setShowForm(false); setEditItem(null); }} onSubmit={onSubmit} />
      </div>
    </div>
  );
};

export default TeacherLiveClasses;

