  border-color: #9ca3af;
}

.cat-btn-continue {
  background: #1e40af;
  color: white;
  border-color: #1e40af;
}

.cat-btn-continue:hover:not(.disabled) {
  background: #1d4ed8;
  border-color: #1d4ed8;
  transform: translateY(-1px);
  box-shadow: 0 4px 8px rgba(30, 64, 175, 0.3);
}

.cat-btn-continue.disabled {
  background: #9ca3af;
  border-color: #9ca3af;
  cursor: not-allowed;
  opacity: 0.6;
}

/* Loading and Error States */
.cat-loading, .cat-error {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  text-align: center;
}

.cat-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid #e5e7eb;
  border-top: 3px solid #1e40af;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-bottom: 20px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.cat-error h3 {
  color: #dc2626;
  margin-bottom: 10px;
}

.cat-error button {
  padding: 8px 16px;
  background: #1e40af;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 11px;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .cat-main-panel {
    grid-template-columns: 1fr;
    height: auto;
  }

  .cat-profile-panel {
    order: -1;
  }

  .declarations-section {
    max-height: 400px;
  }
}

@media (max-width: 768px) {
  .cat-header-top {
    flex-direction: column;
    gap: 10px;
    text-align: center;
  }

  .cat-logos {
    justify-content: center;
  }

  .terms-content {
    padding: 15px;
  }

  .profile-section,
  .declarations-section,
  .action-buttons {
    padding: 15px;
  }

  .action-buttons {
    flex-direction: column;
  }

  .cat-btn {
    width: 100%;
  }

  .sections-table {
    overflow-x: auto;
  }

  .sections-table table {
    min-width: 500px;
  }
}

/* Print Styles */
@media print {
  .cat-terms-page {
    background: white;
  }

  .cat-header {
    background: white !important;
    color: black !important;
    border-bottom: 2px solid black;
  }

  .action-buttons {
    display: none;
  }

  .declarations-section {
    max-height: none;
    overflow: visible;
  }
}

