backend1/routes/devPayment.js.content:
const express = require('express');
const router = express.Router();
const User = require('../models/UserSchema');

// Development payment unlock - no auth required
router.post('/unlock-course-payment', async (req, res) => {
  try {
    console.log('ðŸ”§ Development payment unlock requested');

    const { courseId } = req.body;

    if (!courseId) {
      return res.status(400).json({
        success: false,
        message: 'Course ID required'
      });
    }

    // Find or create demo user with atomic upsert to prevent race conditions
    const demoUserId = '507f1f77bcf86cd799439011';
    const demoEmail = 'demo@test.com';

    let demoUser = await User.findOneAndUpdate(
      { email: demoEmail },
      {
        $setOnInsert: {
          _id: demoUserId,
          email: demoEmail,
          phoneNumber: '9999999999',
          name: 'Demo Student',
          isEmailVerified: true,
          isPhoneVerified: true,
          city: 'Demo City',
          gender: 'Male',
          dob: new Date('1995-01-01'),
          selectedCategory: 'CAT',
          selectedExam: 'CAT 2025',
          enrolledCourses: []
        }
      },
      { upsert: true, new: true }
    );
    console.log('âœ… Demo user ready:', demoUser._id);

    // Check if course is already unlocked
    const existingCourse = demoUser.enrolledCourses.find(
      c => c.courseId && c.courseId.toString() === courseId
    );

    if (existingCourse) {
      // If already present but locked, mark as unlocked
      if (existingCourse.status !== 'unlocked') {
        existingCourse.status = 'unlocked';
        await demoUser.save();
        console.log('âœ… Existing demo enrollment status updated to unlocked for demo user:', demoUser._id);
      }
      return res.status(200).json({
        success: true,
        message: 'Course already unlocked',
        alreadyUnlocked: true,
        enrolledCourses: demoUser.enrolledCourses
      });
    }

    // Add course to enrolled courses
    demoUser.enrolledCourses.push({
      courseId,
      status: 'unlocked',
      enrolledAt: new Date()
    });

    await demoUser.save();
    console.log('âœ… Course unlocked for demo user:', courseId);

    res.status(200).json({
      success: true,
      message: 'Course unlocked successfully',
      courseId,
      userId: demoUser._id,
      enrolledCourses: demoUser.enrolledCourses
    });

  } catch (error) {
    console.error('âŒ Dev payment unlock error:', error);
    res.status(500).json({
      success: false,
      message: 'Server error',
      error: error.message
    });
  }
});

// Get demo user courses - no auth required
router.get('/my-courses', async (req, res) => {
  try {
    console.log('ðŸ”§ Development my-courses requested');

    const demoUserId = '507f1f77bcf86cd799439011';
    const demoUser = await User.findById(demoUserId).populate('enrolledCourses.courseId');

    if (!demoUser) {
      return res.status(200).json({
        success: true,
        courses: []
      });
    }

    const unlockedCourses = demoUser.enrolledCourses
      .filter(c => c.status === "unlocked" && c.courseId)
      .map(c => ({
        _id: c._id,
        status: c.status,
        enrolledAt: c.enrolledAt,
        courseId: c.courseId,
      }));

    res.status(200).json({ 
      success: true, 
      courses: unlockedCourses 
    });

  } catch (error) {
    console.error('âŒ Dev my-courses error:', error);
    res.status(500).json({
      success: false,
      message: 'Server error',
      error: error.message
    });
  }
});

module.exports = router;

