  transition: all 0.2s ease;
  border: 1px solid transparent;
}

/* Subject Colors */
.subject-a {
  background: #dbeafe;
  color: #1e40af;
  border-color: #bfdbfe;
}

.subject-b {
  background: #fef3c7;
  color: #d97706;
  border-color: #fed7aa;
}

