  background: #ede9fe;
  color: #7c3aed;
  border-color: #ddd6fe;
}

.subject-d {
  background: #dcfce7;
  color: #16a34a;
  border-color: #bbf7d0;
}

/* Status variations */
.status-done.subject-a { background: #dcfce7; color: #16a34a; border-color: #bbf7d0; }
.status-done.subject-b { background: #dcfce7; color: #16a34a; border-color: #bbf7d0; }
.status-done.subject-c { background: #dcfce7; color: #16a34a; border-color: #bbf7d0; }
.status-done.subject-d { background: #dcfce7; color: #16a34a; border-color: #bbf7d0; }

.status-pending.subject-a { background: #f3f4f6; color: #6b7280; border-color: #e5e7eb; }
.status-pending.subject-b { background: #f3f4f6; color: #6b7280; border-color: #e5e7eb; }
.status-pending.subject-c { background: #f3f4f6; color: #6b7280; border-color: #e5e7eb; }
.status-pending.subject-d { background: #f3f4f6; color: #6b7280; border-color: #e5e7eb; }

/* Sizes */
.size-small {
