}

.size-large {
  padding: 8px 16px;
  font-size: 14px;
}

/* Interactive */
.subject-chip.interactive {
  cursor: pointer;
}

.subject-chip.interactive:hover {
  transform: translateY(-1px);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.subject-chip.interactive:focus {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

/* Count badge */
.subject-chip-count {
  background: rgba(255, 255, 255, 0.8);
  color: inherit;
  padding: 2px 6px;
  border-radius: 10px;
  font-size: 10px;
  font-weight: 700;
  margin-left: 4px;
}

/* Status Chips */
.status-chip {
  display: inline-flex;
  align-items: center;
  padding: 4px 10px;
  border-radius: 12px;
  font-weight: 500;
  font-size: 12px;
  text-transform: capitalize;
  border: 1px solid transparent;
}

.status-success {
