  border-color: #bbf7d0;
}

.status-warning {
  background: #fef3c7;
  color: #d97706;
  border-color: #fed7aa;
}

.status-danger {
  background: #fee2e2;
  color: #dc2626;
  border-color: #fecaca;
}

.status-primary {
  background: #dbeafe;
  color: #1e40af;
  border-color: #bfdbfe;
}

.status-neutral {
  background: #f3f4f6;
  color: #6b7280;
  border-color: #e5e7eb;
}

/* Batch Chips */
.batch-chip {
  background: white;
  border: 2px solid #e5e7eb;
  border-radius: 12px;
  padding: 12px;
  transition: all 0.2s ease;
  min-width: 140px;
}

.batch-chip.interactive {
  cursor: pointer;
}

.batch-chip.interactive:hover,
.batch-chip.active {
  border-color: #3b82f6;
  box-shadow: 0 2px 8px rgba(59, 130, 246, 0.1);
}

.batch-chip.interactive:focus {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

.batch-chip-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 6px;
}

.batch-chip-name {
  font-weight: 600;
  color: #1f2937;
  font-size: 14px;
}

.batch-chip-meta {
  font-size: 12px;
  color: #6b7280;
}

/* Responsive */
@media (max-width: 768px) {
  .subject-chip {
    padding: 4px 8px;
    font-size: 11px;
  }

  .size-small {
    padding: 3px 6px;
    font-size: 10px;
  }

  .batch-chip {
    min-width: 120px;
    padding: 10px;
  }

  .batch-chip-name {
    font-size: 13px;
  }
}

/* Animation for status changes */
.subject-chip, .status-chip {
  animation: chipIn 0.2s ease-out;
}

@keyframes chipIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

