    try {
      const r = await http.get('/tests/reports', { params: { role: 'student' } });
      const items = r.data?.items || [];
      setItems(items);
      setOffline(false);
      try { sessionStorage.setItem(key, JSON.stringify(items)); } catch {}
    } catch {
      setOffline(true);
    }
