                      const a = document.createElement('a'); a.href=url; a.download=`${(it.testId||'report')}-${(it.studentId||'me')}.pdf`; document.body.appendChild(a); a.click();
                      setTimeout(()=>{ window.URL.revokeObjectURL(url); document.body.removeChild(a); },0);
                    } catch { alert('Failed to download'); }
                  }}>PDF</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* Simple chart */}
      <div className="lc-card lc-section">
        <div className="lc-title">Your Score vs Avg (last 5)</div>
        <div style={{display:'flex',gap:8,alignItems:'flex-end',height:120}}>
          {items.slice(0,5).map((it,idx)=>{
            const h = Math.max(4, Math.min(100, it.score));
            const avg = Math.max(4, Math.min(100, it.avgScore || it.score));
            return (
              <div key={idx} style={{display:'grid',gap:4}}>
                <div style={{display:'flex',gap:4,alignItems:'flex-end'}}>
                  <div style={{width:20,background:'#2d8cff',height:h}} title={`You: ${h}`}></div>
                  <div style={{width:20,background:'#a3c4ff',height:avg}} title={`Avg: ${avg}`}></div>
                </div>
                <div className="lc-muted" style={{maxWidth:40,fontSize:10}}>{it.testName}</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default StudentReports;

backend1/routes/CourseRoute.js.content:
const express = require("express");
const router = express.Router();
const {
  createCourse,
  getCourses,
  getCourseById,
  updateCourse,
  deleteCourse,
  toggleLock,
  togglePublish,
  getPublishedCourses,
  getPublishedCourseById,
  upsertCourseOverview
} = require("../controllers/CourseController");

// âœ… Auth & Permission Middleware
const { authMiddleware } = require("../middleware/authMiddleware");
const { checkPermission } = require("../middleware/permissionMiddleware");

// âœ… Multer setup
const multer = require("multer");

const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    cb(null, "uploads/");
  },
  filename: function (req, file, cb) {
    cb(null, Date.now() + "-" + file.originalname);
  },
});
const upload = multer({ storage });

// âœ… CREATE course with thumbnail
router.post(
  "/",
  authMiddleware,
  checkPermission("course_create"),
  upload.single("thumbnail"),
  createCourse
);

// âœ… PUBLIC routes first (before parameter routes that can match anything)
// âœ… GET published courses for student LMS (no auth needed)
router.get("/student/published-courses", getPublishedCourses);
router.get("/student/published-courses/:id", getPublishedCourseById);

// âœ… READ all courses or by ID (ADMIN - after public routes)
router.get("/", authMiddleware, checkPermission("course_read"), getCourses);

router.get(
  "/:id",
  authMiddleware,
  checkPermission("course_read"),
  getCourseById
);

// âœ… UPDATE course with optional thumbnail
router.put(
  "/:id/overview",
  authMiddleware,
  checkPermission("course_update"),
  upsertCourseOverview
);

router.put(
  "/:id",
  authMiddleware,
  checkPermission("course_update"),
  upload.single("thumbnail"),
  updateCourse
);

// âœ… DELETE course
router.delete(
  "/:id",
  authMiddleware,
  checkPermission("course_delete"),
  deleteCourse
);

// âœ… TOGGLE lock/unlock
router.put(
  "/toggle-lock/:id",
  authMiddleware,
  checkPermission("course_update"),
  toggleLock
);

// âœ… TOGGLE publish/unpublish
router.put(
  "/toggle-publish/:id",
  authMiddleware,
  checkPermission("course_update"),
  togglePublish
);

module.exports = router;

