const mongoose = require("mongoose");

const courseSchema = new mongoose.Schema(
  {
    name: {
      type: String,
      required: true,
      unique: true,
      trim: true,
    },
    description: {
      type: String,
      default: "",
    },
    price: {
      type: Number,
      required: true,
    },
    thumbnail: {
      type: String,
      default: "",
    },
    published: {
  type: Boolean,
  default: false,
},

    isActive: {
      type: Boolean,
      default: true,
    },

    // âœ… NEW: Lock/unlock support (default locked)
    locked: {
      type: Boolean,
      default: true,
    },

    // âœ… NEW: Created by admin or subadmin
    createdBy: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "User", // or "Admin" based on your schema
      required: true,
    },

    // Batch subject rotation support
    startSubject: { type: String, enum: ['A','B','C','D'], default: 'A' },
    subjects: { type: [String], default: ['A','B','C','D'] },
    validityMonths: { type: Number, default: 12 },

    overview: {
      description: { type: String, default: "" },
      about: { type: String, default: "" },
      materialIncludes: { type: [String], default: [] },
      requirements: { type: [String], default: [] },
      videoUrl: { type: String, default: "" },
    },
  },
  { timestamps: true }
);

module.exports = mongoose.model("Course", courseSchema);

