    children
  ) : (
    <Navigate to={tokenName === "adminToken" ? "/admin" : "/subadmin"} />
  );
};

/** ðŸ” Global ScrollToTop on route change */
const ScrollToTop = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo({ top: 0, left: 0, behavior: "auto" });
  }, [pathname]);
  return null;
};

