/* Material Viewer Modal Styles */
.material-viewer-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.6);
  backdrop-filter: blur(4px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2000;
  padding: 1rem;
}

.material-viewer-sheet {
  background: white;
  border-radius: 12px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  width: 100%;
  max-width: 1000px;
  max-height: 90vh;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.material-viewer-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.material-viewer-title {
  margin: 0;
  color: white;
  font-size: 1.3rem;
  font-weight: 600;
}

.material-viewer-close {
  background: none;
  border: none;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: transform 0.2s ease;
}

.material-viewer-close:hover {
  transform: scale(1.1);
}

.material-viewer-body {
  flex: 1;
  overflow-y: auto;
  padding: 1.5rem;
}

.material-viewer-loading,
.material-viewer-error {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 400px;
  font-size: 1rem;
  color: #666;
}

.material-viewer-error {
  color: #d32f2f;
}

.material-viewer-content {
  display: grid;
  grid-template-columns: 300px 1fr;
  gap: 2rem;
}

.material-details {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding-right: 1rem;
  border-right: 1px solid rgba(0, 0, 0, 0.1);
}

.detail-section {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.detail-section label {
  font-weight: 600;
  color: #333;
  font-size: 0.9rem;
}

.detail-section span,
.detail-section p {
  color: #666;
  font-size: 0.95rem;
  word-break: break-word;
}

.detail-section.full-width {
  grid-column: 1 / -1;
  border-right: none;
  padding-right: 0;
}

.material-pdf-container {
  flex: 1;
}

.material-pdf-viewer {
  width: 100%;
  height: 500px;
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 8px;
}

.material-viewer-actions {
  display: flex;
  justify-content: flex-end;
  gap: 1rem;
  padding: 1rem 1.5rem;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
  background: #f8f9fa;
}

.material-viewer-actions .download-btn,
.material-viewer-actions .close-btn {
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  font-size: 0.95rem;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.2s ease;
}

.material-viewer-actions .download-btn {
  background: linear-gradient(135deg, #10b981, #059669);
  color: white;
}

.material-viewer-actions .download-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(16, 185, 129, 0.3);
}

.material-viewer-actions .close-btn {
  background: #e5e7eb;
  color: #333;
}

.material-viewer-actions .close-btn:hover {
  background: #d1d5db;
  transform: translateY(-2px);
}

/* View Button Style */
.view-btn {
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  font-size: 0.95rem;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.2s ease;
}

.view-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
}

.view-btn:active {
  transform: translateY(0);
}

/* Responsive Material Viewer */
@media (max-width: 768px) {
  .material-viewer-sheet {
    max-width: 95vw;
    max-height: 95vh;
  }

  .material-viewer-content {
    grid-template-columns: 1fr;
    gap: 1rem;
  }

  .material-details {
    border-right: none;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    padding-right: 0;
    padding-bottom: 1rem;
  }

  .material-pdf-viewer {
    height: 400px;
  }

  .material-viewer-actions {
    flex-direction: column-reverse;
  }

  .material-viewer-actions button {
    width: 100%;
    justify-content: center;
  }

  .material-viewer-header {
    padding: 1rem;
  }

  .material-viewer-title {
    font-size: 1.1rem;
  }

  .material-viewer-body {
    padding: 1rem;
  }
}

Frontend1/src/pages/Student/MockTests/Mo.content:
/* CAT Exam Style Instructions Page */
.cat-instructions-page {
  min-height: 100vh;
  background: #f5f5f5;
  font-family: Arial, sans-serif;
  font-size: 12px;
  color: #333;
}

/* Header Section */
.cat-header {
  background: linear-gradient(to right, #1e3a8a, #3b82f6, #60a5fa);
  color: white;
  padding: 8px 0;
  border-bottom: 2px solid #1e40af;
}

.cat-header-top {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
}

.cat-logos {
  display: flex;
  gap: 15px;
  align-items: center;
}

.logo-item {
  background: rgba(255, 255, 255, 0.2);
  padding: 4px 12px;
  border-radius: 15px;
  font-weight: bold;
  font-size: 11px;
  border: 1px solid rgba(255, 255, 255, 0.3);
}

.cat-title h1 {
  font-size: 18px;
  font-weight: bold;
  margin: 0;
  text-align: center;
}

.cat-title h2 {
  font-size: 14px;
  font-weight: normal;
  margin: 2px 0 0 0;
  text-align: center;
  opacity: 0.9;
}

/* Main Content */
.cat-content {
  max-width: 1200px;
  margin: 0 auto;
  background: white;
  min-height: calc(100vh - 60px);
}

.cat-main-panel {
  display: grid;
  grid-template-columns: 1fr 280px;
  height: calc(100vh - 60px);
}

/* Left Panel - Instructions */
.cat-instructions-panel {
  padding: 0;
  overflow-y: auto;
  border-right: 2px solid #e5e7eb;
  background: white;
}

.instructions-header {
  background: #f8fafc;
  padding: 12px 20px;
  border-bottom: 1px solid #e5e7eb;
  position: sticky;
  top: 0;
  z-index: 10;
}

.instructions-header h3 {
  margin: 0;
  font-size: 14px;
  font-weight: bold;
  color: #1e40af;
  text-transform: uppercase;
}

.instructions-content {
  padding: 20px;
}

.instruction-section h4 {
  color: #1e40af;
  font-size: 13px;
  font-weight: bold;
  margin: 0 0 15px 0;
  text-decoration: underline;
}

.instruction-list {
  margin: 0;
  padding-left: 18px;
  line-height: 1.5;
}

.instruction-list li {
  margin-bottom: 12px;
  text-align: justify;
  font-size: 11px;
}

/* Question Status Legend */
.question-status-legend {
  margin: 20px 0;
  border: 1px solid #d1d5db;
  border-radius: 4px;
  background: #f9fafb;
  padding: 15px;
}

.status-row {
  display: flex;
  align-items: center;
  margin-bottom: 8px;
}

.status-item {
  display: flex;
  align-items: center;
  gap: 12px;
  width: 100%;
}

.status-box {
  width: 24px;
  height: 24px;
  border-radius: 3px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 11px;
  font-weight: bold;
  border: 1px solid #ccc;
}

.status-box.not-visited {
  background: #f3f4f6;
  color: #374151;
  border-color: #9ca3af;
}

.status-box.not-answered {
  background: #fef3c7;
  color: #92400e;
  border-color: #f59e0b;
}

.status-box.answered {
  background: #dcfce7;
  color: #166534;
  border-color: #22c55e;
}

.status-box.review {
  background: #fce7f3;
  color: #be185d;
  border-color: #ec4899;
}

.status-box.answered-review {
  background: #dbeafe;
  color: #1d4ed8;
  border-color: #3b82f6;
}

.status-box.marked-review {
  background: #f3e8ff;
  color: #7c3aed;
  border-color: #8b5cf6;
}

.status-label {
  font-size: 11px;
  color: #374151;
  flex: 1;
}

/* Right Panel - Profile & Actions */
.cat-profile-panel {
  background: #f8fafc;
  padding: 0;
  border-left: 1px solid #e5e7eb;
  display: flex;
  flex-direction: column;
}

.profile-section {
  padding: 20px;
  text-align: center;
  border-bottom: 1px solid #e5e7eb;
  background: white;
}

.profile-avatar {
  width: 80px;
  height: 80px;
  margin: 0 auto 10px;
  border-radius: 50%;
  border: 2px solid #1e40af;
  overflow: hidden;
  background: #f3f4f6;
  display: flex;
  align-items: center;
  justify-content: center;
}

.avatar-placeholder {
  font-size: 32px;
  color: #6b7280;
}

.profile-info h4 {
  margin: 0 0 4px 0;
  font-size: 13px;
  font-weight: bold;
  color: #1f2937;
}

.profile-info p {
  margin: 0;
  font-size: 11px;
  color: #6b7280;
}

.test-details-box {
  padding: 20px;
  background: white;
  margin: 0;
  border-bottom: 1px solid #e5e7eb;
}

.test-details-box h4 {
  margin: 0 0 15px 0;
  font-size: 13px;
  font-weight: bold;
  color: #1e40af;
  text-align: center;
  border-bottom: 1px solid #e5e7eb;
  padding-bottom: 8px;
}

.detail-row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
  font-size: 11px;
}

.detail-row span:first-child {
  color: #6b7280;
  font-weight: normal;
}

.detail-row span:last-child {
  color: #1f2937;
  font-weight: bold;
}

.terms-section {
  padding: 20px;
  background: #fffbeb;
  border: 1px solid #fbbf24;
  margin: 0;
  flex: 1;
}

.cat-checkbox {
  display: flex;
  align-items: flex-start;
  gap: 10px;
  cursor: pointer;
  font-size: 11px;
  line-height: 1.4;
}

.cat-checkbox input[type="checkbox"] {
  display: none;
}

.checkmark {
  width: 16px;
  height: 16px;
  border: 2px solid #1e40af;
  border-radius: 3px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: white;
  margin-top: 1px;
  flex-shrink: 0;
}

.cat-checkbox input[type="checkbox"]:checked + .checkmark {
  background: #1e40af;
}

.cat-checkbox input[type="checkbox"]:checked + .checkmark::after {
  content: 'âœ“';
  color: white;
  font-size: 12px;
  font-weight: bold;
}

.checkbox-text {
  color: #374151;
  font-weight: normal;
}

.action-buttons {
  padding: 20px;
  background: white;
  border-top: 1px solid #e5e7eb;
  display: flex;
  gap: 10px;
  justify-content: center;
}

.cat-btn {
  padding: 8px 20px;
  border: 1px solid #d1d5db;
  border-radius: 4px;
  font-size: 11px;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.2s ease;
  text-transform: uppercase;
}

.cat-btn-back {
  background: #f3f4f6;
  color: #374151;
}

.cat-btn-back:hover {
  background: #e5e7eb;
}

.cat-btn-next {
  background: #1e40af;
  color: white;
  border-color: #1e40af;
}

.cat-btn-next:hover:not(.disabled) {
  background: #1d4ed8;
  border-color: #1d4ed8;
}

.cat-btn-next.disabled {
  background: #9ca3af;
  border-color: #9ca3af;
  cursor: not-allowed;
  opacity: 0.6;
}

/* Loading and Error States */
.cat-loading, .cat-error {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  text-align: center;
}

.cat-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid #e5e7eb;
  border-top: 3px solid #1e40af;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-bottom: 20px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.cat-error h3 {
  color: #dc2626;
  margin-bottom: 10px;
}

.cat-error button {
  padding: 8px 16px;
  background: #1e40af;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 11px;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .cat-main-panel {
    grid-template-columns: 1fr;
    height: auto;
  }

  .cat-profile-panel {
    order: -1;
  }

  .instructions-header {
    position: static;
  }
}

@media (max-width: 768px) {
  .cat-header-top {
    flex-direction: column;
    gap: 10px;
    text-align: center;
  }

  .cat-logos {
    justify-content: center;
  }

  .instructions-content {
    padding: 15px;
  }

  .profile-section,
  .test-details-box,
  .terms-section,
  .action-buttons {
    padding: 15px;
  }

  .action-buttons {
    flex-direction: column;
  }

  .cat-btn {
    width: 100%;
  }
}

/* Print Styles */
@media print {
  .cat-instructions-page {
    background: white;
  }

  .cat-header {
    background: white !important;
    color: black !important;
    border-bottom: 2px solid black;
  }

  .action-buttons {
    display: none;
  }
}

