/* Container Layout */
.tc-course-tree-container {
  padding: 20px;
  max-width: 1200px;
  margin: auto;
}

.tc-course-tree-title {
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 20px;
  text-align: center;
}

/* Grid Layout */
.tc-course-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
}

/* Course Card */
.tc-course-card {
  background: #fff;
  border: 1px solid #ddd;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.06);
  transition: transform 0.2s ease;
  display: flex;
  flex-direction: column;
}

.tc-course-card:hover {
  transform: translateY(-4px);
}

/* Image */
.tc-course-image {
  width: 100%;
  height: 180px;
  object-fit: cover;
  border-bottom: 1px solid #eee;
}

/* Info Section */
.tc-course-info {
  padding: 16px;
  flex: 1;
}

.tc-course-name {
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 8px;
  color: #000;
  font-family: DM serif Display;
  display: flex;
}

.tc-course-desc {
