backend1/models/course/Topic.js.content:
 const mongoose=require("mongoose")

const topicSchema = new mongoose.Schema({
  course: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Course",
    required: true
  },
  subject: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Subject",
    required: true
  },
  chapter: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Chapter",
    required: true
  },
  name: {
    type: String,
    required: true,
    trim: true
  },
  description: {
    type: String,
    default: ""
  },
  order: {
    type: Number,
    default: 0
  },
  isFullTestSection: {
    type: Boolean,
    default: false
  },
  isActive: {
    type: Boolean,
    default: true
  }
}, { timestamps: true });


module.exports = mongoose.model("Topic", topicSchema);

backend1/routes/QuestionRoute.js.content:
const express = require("express");
const router = express.Router();

const {
  createQuestion,
  getQuestionsByTest,
  updateQuestion,
  deleteQuestion,

} = require("../controllers/QuestionController");

const { authMiddleware } = require("../middleware/authMiddleware");
const { checkPermission } = require("../middleware/permissionMiddleware");


// console.log("âœ… authMiddleware:", typeof authMiddleware);
// console.log("âœ… checkPermission:", typeof checkPermission);

// âœ… Create question
router.post("/", authMiddleware, checkPermission("question_create"), createQuestion);

// âœ… Get questions of a test (query parameter format)
router.get("/", authMiddleware, checkPermission("question_read"), getQuestionsByTest);

// âœ… Get questions of a test (path parameter format - for backward compatibility)
router.get("/:testId", authMiddleware, checkPermission("question_read"), getQuestionsByTest);

// âœ… Update question
router.put("/:id",authMiddleware, checkPermission("question_update"), updateQuestion);

// âœ… Delete question
router.delete("/:id", authMiddleware, checkPermission("question_delete"), deleteQuestion);




module.exports = router;

