/* Wrapper */
.MAT-wrap {
  max-width: 1080px;
  margin: 32px auto 64px;
  padding: 0 20px;
  font-family: "Segoe UI", sans-serif;
  color: #1c1c1c;
}

/* Title */
.MAT-title {
  font-size: 40px;
  line-height: 1.2;
  font-weight: 700;
  margin-bottom: 16px;
  color: #0b0b0b;
}

/* Responsive YouTube */
.MAT-video {
  position: relative;
  width: 100%;
  aspect-ratio: 16 / 9;
  background: #000;
  border-radius: 12px;
  overflow: hidden;
  margin: 14px 0 22px;
}
.MAT-video iframe {
  width: 100%;
  height: 100%;
  border: 0;
  display: block;
}

/* Headings & text */
.MAT-h2 {
  font-size: 26px;
  font-weight: 700;
  margin: 26px 0 10px;
  color: #0b278f;
}

.MAT-p {
  font-size: 16px;
  line-height: 1.7;
  margin-bottom: 12px;
}

/* Lists */
.MAT-list,
.MAT-olist {
  margin: 8px 0 14px 20px;
  font-size: 16px;
  line-height: 1.7;
}
.MAT-sublist {
  margin: 6px 0 0 18px;
}

/* Notes & muted text */
.MAT-muted {
  font-size: 14px;
  color: #666;
}
.MAT-note {
  margin: 16px 0 22px;
  padding: 12px 16px;
  background: #f8f8ff;
  border-left: 4px solid #0b278f;
  border-radius: 6px;
  font-size: 15px;
  line-height: 1.6;
}

/* Mobile tweaks */
@media (max-width: 520px) {
  .MAT-title { font-size: 30px; }
  .MAT-h2 { font-size: 22px; }
  .MAT-p, .MAT-list, .MAT-olist { font-size: 15px; }
}

