Frontend1/src/pages/CourseDetails/Course.content:
/* Base Styles - Mobile First Approach */
html,
body {
  max-width: 100%;
  overflow-x: hidden;
}

/* ========================================
   HERO SECTION
======================================== */
.tgz-hero-container {
  display: flex;
  flex-direction: column;
  height: auto;
  background-color: #1a237e;
  min-height: 100vh;
}

.tgz-hero-left {
  width: 100%;
  background-color: #1a237e;
  color: white;
  padding: 30px 20px;
  order: 1;
}

.tgz-hero-heading {
  font-size: 28px;
  font-weight: 700;
  line-height: 1.2;
  text-align: left;
  margin-bottom: 15px;
}

.tgz-highlight,
.tgz-highlight1 {
  color: #ffa726;
  display: inline;
  font-weight: 700;
}

.tgz-hero-subtext {
  font-size: 16px;
  margin-top: 15px;
  font-weight: 500;
  text-align: left;
  color: #ffffff;
  line-height: 1.5;
}
/* 
.tgz-hero-primary-btn {
  margin: 20px 0;
  padding: 12px 24px;
  font-size: 16px;
  background-color: #d8583f;
  color: white;
  border: none;
  border-radius: 6px;
  font-weight: 600;
  cursor: pointer;
  width: 100%;
  max-width: 200px;
} */
.tgz-hero-primary-btn {
  margin: 20px 0;
  padding: 12px 32px;
  font-size: 16px;
  background-color: #d8583f;
  color: white;
  border: none;
  border-radius: 6px;
  font-weight: 600;
  cursor: pointer;
  white-space: nowrap;      /* ðŸ‘ˆ One line only */
  display: inline-block;    /* ðŸ‘ˆ Prevent full width */
  text-align: center;
}

.tgz-hero-buttons {
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* Always 3 buttons per row */
  gap: 14px;
  margin-top: 20px;
  max-width: 730px;
  margin-left: auto;
  margin-right: auto;
}
@media (max-width: 640px) {
  .tgz-hero-buttons {
    grid-template-columns: repeat(2, 1fr); /* Show 2 buttons per row */
    gap: 12px;
    margin-left: -10px
  }
}

/* ðŸ“± Ultra Small Screens (below 420px) */
@media (max-width: 420px) {
  .tgz-hero-buttons {
    grid-template-columns: 1fr; /* Show 1 button per row */
  }
}


.tgz-hero-secondary-btn {
  background-color: #ffffff;
  color: #081c59;
  border: 2px solid #081c59;
  border-radius: 6px;
  padding: 8px 12px;         /* ðŸ‘ˆ Reduced horizontal & vertical padding */
  font-size: 14px;           /* ðŸ‘ˆ Corrected from 104px to 14px */
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  white-space: nowrap;
  width: 100%;
}


.tgz-hero-secondary-btn {
  flex: 1 1 calc(50% - 4px);
  min-width: 120px;
  padding: 8px 16px;
  background-color: white;
  color: #101c64;
  border: none;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 500;
  cursor: pointer;
  text-align: center;
}

.tgz-hero-right {
  width: 100%;
  background-color: #ff9d23;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 30px 20px;
  border-top-left-radius: 20px;
  order: 2;
}

.tg-hero-video-wrapper {
  background-color: white;
  border-radius: 18px;
  overflow: hidden;
  height: 420px;
  width: 600px;
  aspect-ratio: 16 / 9;
  display: flex;
  justify-content: center;
  align-items: center;
}

.tg-hero-video {
  width: 100%;
  height: 100%;
  border: none;
}

/* ========================================
   COURSE SECTION
======================================== */
.tg-course-container {
  display: flex;
  flex-direction: column;
  gap: 30px;
  padding: 40px 20px;
  background-color: #fff;
}

.tg-course-left,
.tg-course-right {
  width: 100%;
}

.tg-course-subheading {
  color: #ee6a4d;
  font-weight: 500;
  letter-spacing: 1px;
  font-size: 16px;
  margin-bottom: 10px;
  text-align: left;
}

.tg-course-heading {
  font-size: 32px;
  font-weight: 400;
  margin-bottom: 20px;
  font-family: "DM serif Display";
  color: #000;
  text-align: left;
  line-height: 1.2;
}

.tg-course-filters {
  display: flex;
  gap: 8px;
  overflow-x: auto;
  scrollbar-width: none;
  padding-bottom: 5px;
  -webkit-overflow-scrolling: touch;
}

.tg-course-filters::-webkit-scrollbar {
  display: none;
}

.tg-filter-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background-color: #101c64;
  color: white;
  padding: 0 12px;
  font-size: 12px;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  font-weight: 500;
  height: 36px;
  min-width: 100px;
  white-space: nowrap;
  flex-shrink: 0;
}

.tg-filter-btn.tg-icon {
  background-color: white;
  color: #101c64;
  border: 1px solid #101c64;
  min-width: 120px;
}

.tg-course-desc {
  font-size: 14px;
  font-weight: 500;
  line-height: 1.6;
  color: #000;
  text-align: left;
  margin-top: 20px;
}

/* ========================================
   PROGRAMS GRID
======================================== */
.programs-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 20px;
  margin: 30px 0;
}

.program-card {
  background: #ffeede;
  border-radius: 20px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 20px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  transition: all 0.3s ease-in-out;
}

.program-card:hover {
  transform: translateY(-3px);
}

.program-image {
  position: relative;
  border-radius: 12px;
  overflow: hidden;
  width: 100%;
  height: 200px;
}

.program-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.program-image .badge {
  position: absolute;
  top: 12px;
  left: 12px;
  background: #1d239f;
  color: #fff;
  padding: 6px 14px;
  font-size: 12px;
  font-weight: 600;
  border-radius: 8px;
}

.program-image .badge2 {
  position: absolute;
  bottom: 15px;
  left: 15px;
  color: #fff;
  font-size: 36px;
  font-family: "DM serif Display";
  font-weight: 400;
}

.program-content {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.program-content h3 {
  font-size: 20px;
  font-weight: 700;
  color: #000;
  text-align: left;
  margin: 0;
}

.program-content ul {
  padding-left: 18px;
  margin: 0;
  text-align: left;
}

.program-content ul li {
  margin-bottom: 8px;
  font-size: 14px;
  font-weight: 600;
  color: #000;
  line-height: 1.5;
}

.program-price-row {
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin-top: 15px;
}

.program-price-row h4 {
  color: #000;
  font-size: 18px;
  font-weight: bold;
  margin: 0;
}

.program-price-row del {
  font-size: 14px;
  color: #888;
}

.program-price-row button {
  padding: 12px 24px;
  background-color: #db4e32;
  color: #fff;
  border: none;
  font-size: 16px;
  font-weight: 600;
  border-radius: 8px;
  cursor: pointer;
  width: 100%;
  text-align: center;
  transition: background-color 0.3s ease;
}

.program-price-row button:hover {
  background-color: #c8422a;
}

.show-all-button {
  margin-top: 30px;
  text-align: center;
}

.show-all-button button {
  padding: 12px 24px;
  background-color: #1d239f;
  color: #fff;
  border: none;
  font-size: 16px;
  font-weight: 600;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.show-all-button button:hover {
  background-color: #151a8a;
}

/* ========================================
   COMPARISON TABLE
======================================== */
.tg-compare-container {
  background: #fff;
  width: 100%;
  padding: 20px;
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
}

.tg-compare-heading {
  font-size: 28px;
  font-weight: 400;
  color: #000;
  font-family: "DM Serif Display", serif;
  text-align: center;
  margin: 30px 0;
}

.tg-compare-table {
  width: 100%;
  border-collapse: collapse;
  margin: 20px 0;
  min-width: 600px;
}

.tg-compare-header th {
  background: #fff;
  color: #000;
  padding: 8px;
  font-size: 12px;
  font-weight: 800;
  text-align: center;
  border: 1px solid #333;
}

.tg-compare-table th,
.tg-compare-table td {
  border: 1px solid #ddd;
  padding: 8px;
  vertical-align: middle;
  font-size: 12px;
  color: #000;
  text-align: left;
}

.tg-compare-feature-cell {
  font-weight: 700;
  text-align: left;
  padding-left: 8px;
  background-color: #f7f7f7;
}

.tg-compare-value-cell {
  font-weight: 600;
  background-color: #fff;
  color: #000;
  text-align: center;
}

.tg-compare-value-cell.buy-now {
  background-color: green;
  color: white;
  font-weight: bold;
}

.tg-vertical-scroll {
  max-height: 400px;
  overflow-y: auto;
  border: 1px solid #ccc;
}

