  font-family: "Manrope", sans-serif;
}

.tc-course-title {
  font-family: DM Serif Display;
  font-weight: 400;
  font-size: 28px;
  color: #000;
  text-align:left;
  margin-bottom: 30px;
}

.tc-course-grid {
  display: grid;
  gap: 15px;
  grid-template-columns: 1fr;
}

.tc-card {
  background: linear-gradient(138.06deg, #ffffff 5.54%, #ebedff 76.34%);
  padding: 20px;
  border-radius: 14px;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.05);
  transition: transform 0.3s ease;
}

.tc-card:hover {
  transform: translateY(-3px);
}

.tc-card h4 {
  font-size: 16px;
  font-weight: 700;
  color: #1a1a1a;
  margin-bottom: 12px;
  text-align: center;
