backend1/models/UserProgress.js.content:
const mongoose = require("mongoose");

const userProgressSchema = new mongoose.Schema(
  {
    userId: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "User",
      required: true,
    },
    courseId: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "Course",
      required: true,
    },
    lessonProgress: [
      {
        lessonId: {
          type: String, // Can be testId, materialId, etc.
          required: true,
        },
        lessonType: {
          type: String,
          enum: ["test", "video", "pdf", "notes"],
          required: true,
        },
        status: {
          type: String,
          enum: ["not_started", "in_progress", "completed"],
          default: "not_started",
        },
        progress: {
          type: Number, // Percentage (0-100)
          default: 0,
        },
        lastAccessedAt: {
          type: Date,
          default: Date.now,
        },
        completedAt: {
          type: Date,
          default: null,
        },
        timeSpent: {
          type: Number, // in seconds
          default: 0,
        },
      },
    ],
    lastAccessedLesson: {
      lessonId: String,
      lessonType: String,
      accessedAt: {
        type: Date,
        default: Date.now,
      },
    },
    overallProgress: {
      type: Number, // Percentage (0-100)
      default: 0,
    },
    totalTimeSpent: {
      type: Number, // Total time spent in seconds
      default: 0,
    },
  },
  { timestamps: true }
);

// Compound index for efficient queries
userProgressSchema.index({ userId: 1, courseId: 1 }, { unique: true });

// Method to update lesson progress
userProgressSchema.methods.updateLessonProgress = function(lessonId, lessonType, progressData) {
  const existingLesson = this.lessonProgress.find(
    (lesson) => lesson.lessonId === lessonId
  );

  if (existingLesson) {
    // Update existing lesson progress
    Object.assign(existingLesson, progressData, {
      lastAccessedAt: new Date(),
    });
  } else {
    // Add new lesson progress
    this.lessonProgress.push({
      lessonId,
      lessonType,
      ...progressData,
      lastAccessedAt: new Date(),
    });
  }

  // Update last accessed lesson
  this.lastAccessedLesson = {
    lessonId,
    lessonType,
    accessedAt: new Date(),
  };

  // Recalculate overall progress
  this.calculateOverallProgress();
};

// Method to calculate overall progress
userProgressSchema.methods.calculateOverallProgress = function() {
  if (this.lessonProgress.length === 0) {
    this.overallProgress = 0;
    return;
  }

  const totalProgress = this.lessonProgress.reduce(
    (sum, lesson) => sum + lesson.progress,
    0
  );
  this.overallProgress = Math.round(totalProgress / this.lessonProgress.length);
};

// Method to get resume lesson (last accessed or first incomplete)
userProgressSchema.methods.getResumeLesson = function() {
  // If there's a last accessed lesson, return it
  if (this.lastAccessedLesson && this.lastAccessedLesson.lessonId) {
    return this.lastAccessedLesson;
  }

  // Otherwise, find first incomplete lesson
  const incompleteLesson = this.lessonProgress.find(
    (lesson) => lesson.status !== "completed"
  );

  if (incompleteLesson) {
    return {
      lessonId: incompleteLesson.lessonId,
      lessonType: incompleteLesson.lessonType,
    };
  }

  return null;
};

module.exports = mongoose.model("UserProgress", userProgressSchema);

Frontend1/src/components/shared/StatusCa.content:
.status-card {
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 16px;
  padding: 20px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
  transition: all 0.2s ease;
  min-height: 120px;
  display: flex;
  align-items: center;
}

.status-card:hover {
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
  transform: translateY(-1px);
}

.status-card-clickable {
  cursor: pointer;
}

.status-card-clickable:focus {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

.status-card-content {
  display: flex;
  align-items: center;
  width: 100%;
  gap: 16px;
}

.status-card-icon {
  flex-shrink: 0;
  width: 48px;
  height: 48px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
}

.status-card-info {
  flex: 1;
  min-width: 0;
}

.status-card-title {
  font-size: 14px;
  font-weight: 600;
  color: #6b7280;
  margin: 0 0 8px 0;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.status-card-value {
  font-size: 28px;
  font-weight: 700;
  color: #1f2937;
  margin: 0 0 4px 0;
  display: flex;
  align-items: center;
  gap: 8px;
}

.status-card-subtitle {
  font-size: 13px;
  color: #9ca3af;
  margin: 0;
  line-height: 1.4;
}

.status-card-trend {
  font-size: 14px;
  font-weight: 600;
  padding: 2px 6px;
  border-radius: 6px;
  display: inline-flex;
  align-items: center;
  gap: 2px;
}

.trend-up {
  color: #16a34a;
  background: #dcfce7;
}

.trend-down {
  color: #dc2626;
  background: #fee2e2;
}

.trend-neutral {
  color: #6b7280;
  background: #f3f4f6;
}

/* Variants */
.status-card-primary .status-card-icon {
  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
}

.status-card-success .status-card-icon {
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
}

.status-card-warning .status-card-icon {
  background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
}

.status-card-danger .status-card-icon {
  background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
}

.status-card-info .status-card-icon {
  background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
}

/* Loading skeleton */
.status-card-loading {
  background: #f9fafb;
  border: 1px solid #e5e7eb;
  cursor: default;
}

.status-card-loading:hover {
  transform: none;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
}

.status-card-skeleton {
  display: flex;
  align-items: center;
  gap: 16px;
  width: 100%;
}

.skeleton-icon {
  width: 48px;
  height: 48px;
  border-radius: 12px;
  background: linear-gradient(90deg, #e5e7eb 25%, #f3f4f6 50%, #e5e7eb 75%);
  background-size: 200% 100%;
  animation: skeleton-loading 1.5s infinite;
}

.skeleton-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.skeleton-title {
  height: 12px;
  width: 60%;
  border-radius: 6px;
  background: linear-gradient(90deg, #e5e7eb 25%, #f3f4f6 50%, #e5e7eb 75%);
  background-size: 200% 100%;
  animation: skeleton-loading 1.5s infinite;
}

.skeleton-value {
  height: 20px;
  width: 40%;
  border-radius: 6px;
  background: linear-gradient(90deg, #e5e7eb 25%, #f3f4f6 50%, #e5e7eb 75%);
  background-size: 200% 100%;
  animation: skeleton-loading 1.5s infinite;
}

.skeleton-subtitle {
  height: 10px;
  width: 80%;
  border-radius: 6px;
  background: linear-gradient(90deg, #e5e7eb 25%, #f3f4f6 50%, #e5e7eb 75%);
  background-size: 200% 100%;
  animation: skeleton-loading 1.5s infinite;
}

@keyframes skeleton-loading {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

/* Responsive */
@media (max-width: 768px) {
  .status-card {
    padding: 16px;
    min-height: 100px;
  }

  .status-card-content {
    gap: 12px;
  }

  .status-card-icon {
    width: 40px;
    height: 40px;
    font-size: 20px;
  }

  .status-card-value {
    font-size: 24px;
  }
}

