// Get complete course structure for students (optimized single call)
exports.getStudentCourseStructure = async (req, res) => {
  try {
    const { courseId } = req.params;
    const userId = req.user.id;

    // Check course access
    const accessCheck = await checkCourseAccess(userId, courseId);
    if (!accessCheck.hasAccess) {
      return res.status(403).json({
        success: false,
        message: accessCheck.message
      });
    }

    // Get complete course structure
    const subjects = await Subject.find({ courseId }).sort({ order: 1 });
    const chapters = await Chapter.find({ courseId }).sort({ order: 1 });
    const topics = await Topic.find({ courseId }).sort({ order: 1 });

    // Fix: Use correct field names from Test model schema
    const tests = await Test.find({ course: courseId }).sort({ title: 1 });

    console.log('ðŸ” DEBUG: Checking for tests in course:', courseId);
    console.log('ðŸ“Š Subjects found:', subjects.length);
    console.log('ðŸ“Š Chapters found:', chapters.length);
    console.log('ðŸ“Š Topics found:', topics.length);
    console.log('ðŸ“Š Tests found:', tests.length);

    // Check for tests associated with different entities
    const allTests = await Test.find({}).limit(20);
    console.log('ðŸ§ª Sample tests in database:');
    allTests.forEach((test, i) => {
      console.log(`  Test ${i}: ${test.title} | Course: ${test.course} | Chapter: ${test.chapter} | Topic: ${test.topic}`);
    });

    // Check for tests that might be associated directly with chapters
    const chaptersWithTests = await Test.find({
      $or: [
        { course: courseId },
        { chapter: { $in: chapters.map(c => c._id) } }
      ]
    });
    console.log('ðŸ§ª Tests associated with this course or its chapters:', chaptersWithTests.length);
    chaptersWithTests.forEach((test, i) => {
      console.log(`  Test ${i}: ${test.title} | Course: ${test.course} | Chapter: ${test.chapter} | Topic: ${test.topic}`);
    });

    // Organize the structure
    const courseStructure = subjects.map(subject => ({
      ...subject.toObject(),
      chapters: chapters
        .filter(chapter => chapter.subjectId.toString() === subject._id.toString())
        .map(chapter => ({
          ...chapter.toObject(),
          topics: topics
            .filter(topic => topic.chapterId.toString() === chapter._id.toString())
            .map(topic => ({
              ...topic.toObject(),
              tests: tests.filter(test => test.topic && test.topic.toString() === topic._id.toString())
            }))
        }))
    }));

    // Also check for tests that might be directly associated with chapters (if no topics exist)
    courseStructure.forEach(subject => {
      subject.chapters.forEach(chapter => {
        if (chapter.topics.length === 0) {
          // If no topics, attach tests directly to chapter
          const chapterTests = tests.filter(test => test.chapter && test.chapter.toString() === chapter._id.toString());
          if (chapterTests.length > 0) {
            // Create a default topic for chapter-level tests
            chapter.topics.push({
              _id: `${chapter._id}_tests`,
              name: `${chapter.name} Tests`,
              chapterId: chapter._id,
              tests: chapterTests,
              createdAt: chapter.createdAt,
              updatedAt: chapter.updatedAt
            });
          }
        }
      });
    });

    res.status(200).json({
      success: true,
      course: accessCheck.course,
      structure: courseStructure
    });
  } catch (error) {
    console.error("Error fetching student course structure:", error);
    res.status(500).json({
      success: false,
      message: "Failed to fetch course structure"
    });
  }
};

Frontend1/src/components/AdminTheme/Admi.content:
import { useEffect, useState } from "react";

function getContrastRatio(hex1, hex2) {
  const toRgb = (hex) => {
    const h = hex.replace('#','');
    const bigint = parseInt(h.length === 3 ? h.split('').map(c=>c+c).join('') : h, 16);
    return { r: (bigint >> 16) & 255, g: (bigint >> 8) & 255, b: bigint & 255 };
  };
  const luminance = ({r,g,b}) => {
    const a = [r,g,b].map(v => {
      v /= 255;
      return v <= 0.03928 ? v/12.92 : Math.pow((v+0.055)/1.055, 2.4);
    });
    return 0.2126*a[0] + 0.7152*a[1] + 0.0722*a[2];
  };
  try {
    const L1 = luminance(toRgb(hex1));
    const L2 = luminance(toRgb(hex2));
    const [max, min] = L1 > L2 ? [L1, L2] : [L2, L1];
    return (max + 0.05) / (min + 0.05);
  } catch {
    return 7; // assume pass
  }
}

export default function AdminThemeProvider() {
  const [checked, setChecked] = useState(false);

  useEffect(() => {
    const root = document.querySelector('.admin-theme');
    if (!root) return;

    const body = document.body;
    const cs = getComputedStyle(body);

    const detectedFont = cs.fontFamily || 'system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial';

    const fallbacks = {
      primary: '#1A237E',
      secondary: '#3949AB',
      accent: '#FF6B35',
      bg: '#f6f8ff',
      surface: '#ffffff',
      surface2: '#121850',
      text: '#0f172a',
      muted: '#64748b',
      border: '#e5e7eb',
      success: '#16a34a',
      warning: '#f59e0b',
      danger: '#dc2626',
    };

    const prefer = (varName, fallback) => getComputedStyle(document.documentElement).getPropertyValue(varName)?.trim() || fallback;

    const tokens = {
      font: detectedFont,
      primary: prefer('--tatha-primary', fallbacks.primary),
      secondary: prefer('--tatha-secondary', fallbacks.secondary),
      accent: prefer('--tatha-accent', fallbacks.accent),
      bg: prefer('--tatha-bg', fallbacks.bg),
      surface: prefer('--tatha-surface', fallbacks.surface),
      surface2: prefer('--tatha-surface-2', fallbacks.surface2),
      text: prefer('--tatha-text', fallbacks.text),
      muted: prefer('--tatha-text-muted', fallbacks.muted),
      border: prefer('--tatha-border', fallbacks.border),
      success: prefer('--tatha-success', fallbacks.success),
      warning: prefer('--tatha-warning', fallbacks.warning),
      danger: prefer('--tatha-danger', fallbacks.danger),
    };

    // Apply variables to root of admin pages
    const apply = (name, value) => root.style.setProperty(name, value);
    apply('--admin-font-sans', tokens.font);
    apply('--admin-primary', tokens.primary);
    apply('--admin-secondary', tokens.secondary);
    apply('--admin-accent', tokens.accent);
    apply('--admin-bg', tokens.bg);
    apply('--admin-surface', tokens.surface);
    apply('--admin-surface-2', tokens.surface2);
    apply('--admin-text', tokens.text);
    apply('--admin-text-muted', tokens.muted);
    apply('--admin-border', tokens.border);
    apply('--admin-success', tokens.success);
    apply('--admin-warning', tokens.warning);
    apply('--admin-danger', tokens.danger);

    // Theme check
    const results = { ok: true, details: {} };
    results.details.fontLoaded = !!tokens.font;

    results.details.primaryMatch = !!tokens.primary;
    results.details.secondaryMatch = !!tokens.secondary;

    // Buttons/chips state check: ensure focus ring visible (contrast)
    const contrast = getContrastRatio(tokens.primary.replace(/\s/g,''), '#ffffff');
    results.details.buttonContrastAA = contrast >= 4.5;

    // Icon contrast on tinted circle over surface
    const surfaceHex = tokens.surface.replace(/\s/g,'');
    const iconOnSurface = getContrastRatio(tokens.primary, surfaceHex);
    results.details.iconContrastAA = iconOnSurface >= 3; // UI icons can pass AA at 3:1

    results.ok = Object.values(results.details).every(Boolean);

    // Rollback if failed: remove admin-theme class to revert to original styles
    if (!results.ok) {
      root.classList.add('admin-theme-failed');
      root.classList.remove('admin-theme');
    }

    // Attach results for Theme Check component to read
    root.dataset.themeCheck = JSON.stringify(results);

    setChecked(true);
  }, []);

  return null;
}

