/* ===== Modal Overlay ===== */
.tf-modal-overlay {
  position: fixed;
  inset: 0;
  background: rgba(10, 12, 40, 0.55);
  backdrop-filter: blur(2px);
  display: grid;
  place-items: center;
  z-index: 9999;
  padding: 18px;
}

/* ===== Modal Box ===== */
.tf-modal {
  width: 100%;
  max-width: 780px;
  background: #ffffff;
  border-radius: 18px;
  box-shadow: 0 30px 80px rgba(3, 8, 36, 0.35);
  overflow: hidden;
  animation: tf-pop 160ms ease-out;
}

@keyframes tf-pop {
  from { transform: translateY(8px) scale(.98); opacity: 0; }
  to   { transform: translateY(0)   scale(1);    opacity: 1; }
}

/* ===== Header (gradient + close) ===== */
.tf-modal-header {
  background: linear-gradient(90deg, #0b1051, #1a237e);
  color: #fff;
  padding: 18px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.tf-modal-header h3 {
  margin: 0;
  font-size: 22px;
  font-weight: 800;
}
.tf-modal-close {
  width: 42px;
  height: 42px;
  border-radius: 12px;
  border: 1.5px solid rgba(255,255,255,0.45);
  background: rgba(255,255,255,0.1);
  color: #fff;
  font-size: 18px;
  cursor: pointer;
}

/* ===== Form layout ===== */
.tf-modal-form {
  padding: 22px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 18px 20px;
}
.tf-field { display: flex; flex-direction: column; gap: 8px; }
.tf-field-full { grid-column: 1 / -1; }
.tf-field label {
  font-size: 13px;
  font-weight: 800;
  color: #0b1051;
}
.tf-field input,
.tf-field textarea {
  width: 100%;
  border: 1.5px solid #eef1ff;
  background: #f7f9ff;
  border-radius: 12px;
  padding: 14px 16px;
  font-size: 15px;
  outline: none;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
}
.tf-field textarea { resize: vertical; }
.tf-field input:focus,
.tf-field textarea:focus {
  border-color: #cfd7ff;
  box-shadow: 0 0 0 4px rgba(26,35,126,0.07);
}

/* ===== Actions ===== */
.tf-modal-actions {
  grid-column: 1 / -1;
  display: flex;
  justify-content: flex-end;
