const mongoose = require("mongoose");
require("dotenv").config();

const Connection = async () => {
  try {
    if (!process.env.MONGO_URI) {
      console.warn("âš ï¸ MONGO_URI not found in environment variables");
      console.log("ðŸ“ Running in development mode without database");
      return;
    }

    await mongoose.connect(process.env.MONGO_URI, {
      // useNewUrlParser: true,
      // useUnifiedTopology: true,
    });
    console.log("âœ… Connected to MongoDB Atlas Successfully!");
  } catch (error) {
    console.error("âŒ MongoDB Connection Error:", error);
    console.log("ðŸ”„ Continuing without database for development...");
    // Don't exit in development - allow server to run without DB
    if (process.env.NODE_ENV === 'production') {
      process.exit(1);
    }
  }
};

module.exports = Connection;

