      } catch {}
      return "";
    }

    (async () => {
      const entries = await Promise.all(
        achieversData.map(async (a) => {
          const id = getYouTubeId(a.videoUrl);
          const title = await fetchOne(a.videoUrl);
          let name = parseNameFromTitle(title);
          let score = parseScoreFromTitle(title);

