    { name: "Gourav Sharma", score: "CAT 99.8%ile", image: successtwo },
    { name: "Gourav Sharma", score: "CAT 99.8%ile", image: successtwo },
  ];

  const [index, setIndex] = useState(0);
  const next = () => setIndex((index + 1) % teamImages.length);
  const prev = () => setIndex((index - 1 + teamImages.length) % teamImages.length);

