}

.quick-stat-content h3 {
  font-size: 16px;
  font-weight: 600;
  color: #1f2937;
  margin: 0 0 4px 0;
}

.quick-stat-content p {
  font-size: 14px;
  color: #6b7280;
  margin: 0;
}

.trend-positive {
  color: #16a34a !important;
  font-weight: 600;
}

.trend-negative {
  color: #dc2626 !important;
  font-weight: 600;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .academics-overview {
    padding: 20px;
  }

  .overview-header {
    flex-direction: column;
    align-items: stretch;
    gap: 16px;
  }

  .overview-header-actions {
    justify-content: flex-start;
  }

  .kpi-grid {
    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
    gap: 16px;
  }
}

@media (max-width: 768px) {
  .academics-overview {
    padding: 16px;
  }

  .overview-header-content h1 {
    font-size: 1.75rem;
  }

  .overview-header-content p {
    font-size: 1rem;
  }

  .kpi-grid {
    grid-template-columns: 1fr;
    gap: 12px;
  }

  .batch-chips-grid {
    grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
    gap: 12px;
  }

  .quick-stats-grid {
    grid-template-columns: 1fr;
    gap: 12px;
  }

  .quick-stat-card {
    padding: 16px;
  }

  .quick-stat-icon {
    width: 40px;
    height: 40px;
    font-size: 20px;
  }
}

@media (max-width: 480px) {
  .section-header h2 {
    font-size: 1.25rem;
  }

  .empty-state {
    padding: 40px 16px;
  }

  .batch-chips-grid {
    grid-template-columns: 1fr;
  }
}

/* Focus states for accessibility */
.batch-chip:focus,
.btn:focus {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

/* Animation for loading states */
.loading-enter {
  opacity: 0;
  transform: translateY(10px);
}

.loading-enter-active {
  opacity: 1;
  transform: translateY(0);
  transition: opacity 0.3s ease, transform 0.3s ease;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .quick-stat-card {
    border-width: 2px;
  }

  .empty-state {
    border-width: 3px;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .quick-stat-card,
  .btn-primary,
  .batch-chip-skeleton * {
    transition: none;
    animation: none;
  }
}

