.cocp-material-box h4{font-size:var(--cocp-size-sub);font-weight:var(--cocp-weight-heading);color:#000;margin-bottom:12px;text-align:left}
.cocp-material-list{list-style:disc; text-align:left;padding-left:20px;color:#000;font-size:15px;}
.cocp-material-list li{margin-bottom:8px}

/* tabs */
.cocp-tabs{margin-top:40px}
.cocp-tab-buttons{display:flex;gap:15px;margin-bottom:25px;flex-wrap:wrap}
.cocp-tab-btn{border:2px solid var(--cocp-primary);background:transparent;color:var(--cocp-primary);font-weight:var(--cocp-weight-heading);font-size:var(--cocp-size-body);padding:8px 24px;border-radius:16px;cursor:pointer;transition:.3s}
.cocp-tab-btn:hover{background:#eaeaff}
.cocp-tab-btn.active{background:var(--cocp-primary);color:#fff;border-color:var(--cocp-primary)}

.cocp-tab-content h3,
.cocp-curriculum h3,
.cocp-instructors h3,
.cocp-reviews h3{font-size:var(--cocp-size-h);font-weight:var(--cocp-weight-heading);color:#000;text-align:left;margin-bottom:18px}
.cocp-tab-content p{font-size:var(--cocp-size-body);font-weight:var(--cocp-weight-body);color:#000;text-align:left;margin-bottom:18px}

/* curriculum */
.cocp-curriculum{margin-top:40px}
.cocp-curriculum-item{border:1px solid #ddd;border-radius:10px;margin-bottom:15px;background:#fff;padding:15px 20px;cursor:pointer;transition:all .3s ease}
.cocp-curriculum-item:hover{background:#f9f9ff}
.cocp-curriculum-title{display:flex;justify-content:space-between;font-weight:var(--cocp-weight-heading);font-size:18px;color:var(--cocp-primary)}
.cocp-curriculum-content{margin-top:10px;font-size:var(--cocp-size-body);font-weight:var(--cocp-weight-body);color:#000;text-align:left}
.cocp-arrow{font-size:18px;color:#2f2f2f}

/* instructors */
.cocp-instructor-grid{display:flex;gap:20px;flex-wrap:wrap}
.cocp-instructor-card{flex:1 1 30%;border-radius:15px;overflow:hidden;background:#f0f2ff;box-shadow:0 0 8px rgba(0,0,0,.05);text-align:center;display:flex;flex-direction:column;justify-content:space-between}
.cocp-instructor-img{background:#0c1a66;padding:20px}
.cocp-instructor-img img{width:100%;max-height:250px;object-fit:contain;border-radius:8px}
.cocp-instructor-info{padding:15px;background:#f0f2ff;font-size:var(--cocp-size-body);font-weight:var(--cocp-weight-strong);color:var(--cocp-ink);line-height:1.4;text-align:left}

/* reviews */
.cocp-reviews{margin-top:40px}
.cocp-review-layout{display:flex;flex-direction:column;gap:30px}

.cocp-rating-summary{display:flex;align-items:flex-start;gap:20px}
.cocp-rating-score{font-size:40px;font-weight:var(--cocp-weight-heading);line-height:1}
.cocp-rating-stars{position:relative;display:inline-block;line-height:1;color:orange;font-size:18px;margin:5px 0}
.cocp-stars-outer{color:#ddd}
.cocp-stars-inner{position:absolute;top:0;left:0;width:var(--percent,0%);overflow:hidden;color:orange;white-space:nowrap;pointer-events:none}
.cocp-total-rating{font-size:var(--cocp-size-small);color:#555}

.cocp-rating-bars{flex:1}
.cocp-bar-line{display:flex;align-items:center;font-size:var(--cocp-size-small);margin-bottom:10px}
.cocp-bar-line .cocp-bar{flex:1;height:6px;background:#ddd;margin:0 10px;border-radius:5px;overflow:hidden}
.cocp-bar-line .cocp-fill{background:var(--cocp-primary);height:100%}

.cocp-review-image-box{flex:0 0 55%;background:transparent;display:flex;align-items:center}
.cocp-review-image-box img{width:100%;max-width:600px;height:300px;border-radius:12px;object-fit:contain}

/* extra imgs */
.cocp-reviews-gallery{display:flex;gap:16px;margin-top:18px;flex-wrap:wrap}
.cocp-reviews-gallery img{flex:1 1 calc(33.333% - 11px);max-width:calc(33.333% - 11px);height:240px;object-fit:contain;background:#fff;border:1px solid #e6e6e6;border-radius:12px;box-shadow:0 2px 10px rgba(0,0,0,.05);padding:8px}
@media (max-width:992px){.cocp-reviews-gallery img{flex-basis:calc(50% - 8px);max-width:calc(50% - 8px);height:180px}}

/* journey (optional, not used) */
.cocp-journey{margin-top:40px;display:flex;justify-content:center;background:#080A22}
.cocp-journey-image{width:100%;height:auto;object-fit:cover}

/* mobile */
@media (max-width:768px){
  .row{flex-direction:column;gap:40px}
  .cocp-left{padding-left:0}
  .cocp-video iframe{height:250px}
}

backend1/models/MockTestSeries.js.content:
const mongoose = require('mongoose');

const mockTestSeriesSchema = new mongoose.Schema({
  title: {
    type: String,
    required: [true, 'Mock test series title is required'],
    trim: true,
    maxlength: [100, 'Title cannot exceed 100 characters']
  },
  description: {
    type: String,
    trim: true,
    maxlength: [500, 'Description cannot exceed 500 characters']
  },
  category: {
    type: String,
    enum: ['CAT', 'XAT', 'SNAP', 'NMAT', 'CMAT', 'MAT', 'GMAT', 'General'],
    default: 'CAT'
  },
  thumbnail: {
    type: String,
    default: null
  },
  totalTests: {
    type: Number,
    default: 0
  },
  freeTests: {
    type: Number,
    default: 0
  },
  price: {
    type: Number,
    default: 0
  },
  validity: {
    type: Number, // in days
    default: 365
  },
  isActive: {
    type: Boolean,
    default: true
  },
  isPublished: {
    type: Boolean,
    default: false
  },
  difficulty: {
    type: String,
    enum: ['Easy', 'Medium', 'Hard', 'Mixed'],
    default: 'Mixed'
  },
  tags: [{
    type: String,
    trim: true
  }],
  createdBy: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Admin',
    required: true
  },
  publishedAt: {
    type: Date
  },
  enrolledStudents: [{
    studentId: {
      type: mongoose.Schema.Types.ObjectId,
      ref: 'User'
    },
    enrolledAt: {
      type: Date,
      default: Date.now
    },
    expiresAt: {
      type: Date
    }
  }]
}, {
  timestamps: true
});

// Index for better query performance
mockTestSeriesSchema.index({ category: 1, isPublished: 1 });
mockTestSeriesSchema.index({ createdBy: 1 });
mockTestSeriesSchema.index({ isActive: 1, isPublished: 1 });

// Virtual for enrolled students count
mockTestSeriesSchema.virtual('enrolledCount').get(function() {
  return this.enrolledStudents.length;
});

module.exports = mongoose.model('MockTestSeries', mockTestSeriesSchema);

