/* ===== Popup (Modal) â€” unique prefix: tcmp- ===== */
.tcmp-overlay {
  position: fixed;
  inset: 0;
  background: rgba(0,0,0,.55);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;
  z-index: 9999;
}

.tcmp-modal {
  background: #fff;
  width: min(960px, 100%);
  max-height: 85vh;
  border-radius: 18px;
  box-shadow: 0 20px 60px rgba(0,0,0,.25);
  padding: 20px 20px 16px;
  display: flex;
  flex-direction: column;   /* keep column so the scroller gets leftover height */
  gap: 12px;
  position: relative;
}

.tcmp-close {
  position: absolute;
  top: 10px;
  right: 12px;
  height: 36px;
  width: 36px;
  border-radius: 10px;
  border: 0;
  background: #f2f2f2;
  cursor: pointer;
  font-size: 20px;
  line-height: 1;
}
.tcmp-close:hover { background: #e9e9e9; }

.tcmp-title {
  margin: 4px 36px 0 6px;
  font-size: 22px;
  font-weight: 800;
  color: #0f0f0f;
}

.tcmp-text {
  margin: 2px 6px 4px;
  font-size: 15px;
  line-height: 1.6;
  color: #333;
}/* ðŸ”½ Vertical image scroller (one-by-one) */
.tcmp-scroll {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 12px;
  padding: 8px 6px 12px;
  overflow-y: auto;
  -webkit-overflow-scrolling: touch;
  border-top: 1px solid #eee;
  scroll-snap-type: y mandatory;
}

/* âœ… FIXED SIZE FRAMES FOR ALL IMAGES */
.tcmp-imgwrap {
  flex: 0 0 auto;
  width: 100%;
  height: clamp(220px, 35vh, 320px);   /* <- yahi size fix hai */
  border-radius: 12px;
  overflow: hidden;
  background: #f6f6f6;
  box-shadow: 0 6px 16px rgba(0,0,0,.08);
  scroll-snap-align: start;
}

.tcmp-imgwrap img {
  width: 100%;
  height: 100%;
  object-fit: cover;                   /* fill kare, crop ok */
  /* ðŸ‘‰ agar poori image dekhni ho bina crop: object-fit: contain; background:#fff; */
}

/* Mobile tweak */
@media (max-width: 640px) {
  .tcmp-imgwrap { height: clamp(180px, 30vh, 240px); }
}







/* ===== POPUP (Modal) â€” FINAL, compact + vertical scroll ===== */
.tcmp-overlay{
  position: fixed;
  inset: 0;
  background: rgba(0,0,0,.55);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 16px;
  z-index: 9999;
}

.tcmp-modal{
  background: #fff;
  width: min(680px, 92vw);   /* smaller width */
  max-height: 70vh;          /* smaller height */
  border-radius: 14px;
  box-shadow: 0 18px 50px rgba(0,0,0,.25);
  padding: 14px 14px 12px;
  display: flex;
  flex-direction: column;
  gap: 10px;
  position: relative;
  box-sizing: border-box;
}

.tcmp-close{
  position: absolute;
  top: 8px; right: 10px;
  height: 32px; width: 32px;
  border-radius: 8px;
  border: 0;
  background: #f2f2f2;
  cursor: pointer;
  font-size: 18px; line-height: 1;
}
.tcmp-close:hover{ background:#e9e9e9; }

.tcmp-title{
  margin: 2px 40px 0 4px;   /* space for close button */
  font-size: 20px; font-weight: 800; color:#0f0f0f;
}

.tcmp-text{
  margin: 0 4px 6px;
  font-size: 14px; line-height: 1.6; color:#333;
}

/* ðŸ”½ Vertical one-by-one image scroller (no crop) */
.tcmp-scroll{
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;       /* center the narrower frames */
  gap: 10px;
  padding: 6px 6px 10px;
  overflow-y: auto;
  -webkit-overflow-scrolling: touch;
  border-top: 1px solid #eee;
  scroll-snap-type: y mandatory;
}

/* âœ… Narrow, fixed frames */
.tcmp-imgwrap{
  flex: 0 0 auto;
  width: clamp(220px, 82%, 480px);   /* narrower than modal */
  height: 220px;                     /* fixed frame height */
  border-radius: 12px;
  overflow: hidden;
  background: #fff;
  box-shadow: 0 6px 16px rgba(0,0,0,.08);
  scroll-snap-align: start;

  display: flex;                     /* center image when contain */
  align-items: center;
  justify-content: center;
}

.tcmp-imgwrap img{
  width: 100%;
  height: 100%;
  display: block;
  object-fit: contain;               /* ðŸ‘ˆ full image visible (no crop) */
  background: #fff;                  /* for letterboxing */
}

/* Mobile tweaks */
@media (max-width: 640px){
  .tcmp-modal{ width: 92vw; max-height: 72vh; }
  .tcmp-title{ font-size: 18px; }
  .tcmp-text{ font-size: 13px; }
  .tcmp-imgwrap{ width: 90%; height: 200px; }
}

Frontend1/src/components/AdminUI/TableMi.content:
import React from 'react';
import Card from './Card';

const TableMini = ({ title, columns = [], rows = [] }) => (
  <Card>
    <div style={{marginBottom:8, fontWeight:600}}>{title}</div>
    <div style={{maxHeight: 260, overflow:'auto'}}>
      <table className="table-mini">
        <thead>
          <tr>
            {columns.map((c) => (<th key={c.key}>{c.label}</th>))}
          </tr>
        </thead>
        <tbody>
          {rows.length === 0 && (
            <tr><td colSpan={columns.length} style={{color:'var(--admin-text-muted)', padding:'16px'}}>No data</td></tr>
          )}
          {rows.map((row, i) => (
            <tr key={i}>
              {columns.map((c) => (<td key={c.key}>{c.render ? c.render(row[c.key], row) : row[c.key]}</td>))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  </Card>
);

export default TableMini;

