:root{
  --tgcp-font:"Manrope","Inter","Segoe UI",system-ui,-apple-system,Roboto,Helvetica,Arial,sans-serif;
  --tgcp-weight-heading:700; --tgcp-weight-strong:600; --tgcp-weight-body:500;
  --tgcp-size-title:clamp(22px,2.0vw,28px); --tgcp-size-h:clamp(20px,1.9vw,26px);
  --tgcp-size-sub:18px; --tgcp-size-body:16px; --tgcp-size-small:14px; --tgcp-line:1.55;
  --tgcp-primary:#1A237E; --tgcp-accent:#D8583F; --tgcp-ink:#1d1d1f; --tgcp-ink-2:#111; --tgcp-muted:#888; --tgcp-border:#999;
}
/* reset */
*{margin:0;padding:0;box-sizing:border-box}
body{font-family:var(--tgcp-font);font-size:var(--tgcp-size-body);font-weight:var(--tgcp-weight-body);line-height:var(--tgcp-line);background:#f4f6f8;color:var(--tgcp-ink)}
.container{max-width:1600px;width:100%;margin-bottom:0}
.row{display:flex;gap:30px}

/* page */
.tgcp-page{background:#fff;padding-top:40px}

/* left */
.tgcp-left{flex:70%;padding-left:50px}
.tgcp-video iframe{width:100%;height:400px;border-radius:10px;border:none;margin-bottom:20px}
.tgcp-title{text-transform:uppercase;letter-spacing:.4px;color:var(--tgcp-primary);font-size:var(--tgcp-size-title);font-weight:var(--tgcp-weight-heading);margin-bottom:25px}

/* info grid */
.tgcp-info-grid{display:flex;gap:0;margin-bottom:30px;flex-wrap:wrap}
.tgcp-info-item{display:flex;align-items:center;gap:10px;min-width:220px;flex:1}
.tgcp-ico{font-size:22px;color:#005ae0}
.tgcp-label{font-size:var(--tgcp-size-small);color:var(--tgcp-muted);font-weight:var(--tgcp-weight-body)}
.tgcp-val{font-size:var(--tgcp-size-body);color:var(--tgcp-ink-2);font-weight:var(--tgcp-weight-strong)}

/* right */
.tgcp-right{flex:0 0 30%;display:flex;flex-direction:column;gap:30px}
.tgcp-course-info{background:#fff;padding:25px;border-radius:12px;border:1px solid var(--tgcp-border);box-shadow:0 0 10px rgba(0,0,0,.05)}
.tgcp-course-title{background:#EFF0FF;color:var(--tgcp-primary);font-weight:var(--tgcp-weight-heading);padding:12px 15px;border-radius:8px;font-size:var(--tgcp-size-title);text-align:center;margin-bottom:25px}
.tgcp-buy-btn{
  width:100%;
  padding:10px 12px;
  background:#1d0a71;
  color:#fff;
  font-weight:var(--tgcp-weight-heading);
  border:none;
  border-radius:8px;
  cursor:pointer;
  font-size:var(--tgcp-size-body);
  margin-top:15px;
  transition:background .3s ease
}

.tgcp-buy-btn:hover{background:#1d0a71}

.tgcp-desc-scroll{max-height:220px;overflow-y:auto;padding-right:5px}
.tgcp-desc-scroll::-webkit-scrollbar{width:0;background:transparent}
.tgcp-material-box{background:#fff;padding:25px;border:1px solid var(--tgcp-border);border-radius:12px;box-shadow:0 0 10px rgba(0,0,0,.05)}
.tgcp-material-box h4{font-size:var(--tgcp-size-sub);font-weight:var(--tgcp-weight-heading);color:#000;margin-bottom:12px;text-align:left}
.tgcp-material-list{list-style:disc; text-align: left;padding-left:20px;color:#000;font-size:15px;}
.tgcp-material-list li{margin-bottom:8px}

/* tabs */
.tgcp-tabs{margin-top:40px}
.tgcp-tab-buttons{display:flex;gap:15px;margin-bottom:25px;flex-wrap:wrap}
.tgcp-tab-btn{border:2px solid var(--tgcp-primary);background:transparent;color:var(--tgcp-primary);font-weight:var(--tgcp-weight-heading);font-size:var(--tgcp-size-body);padding:8px 24px;border-radius:16px;cursor:pointer;transition:.3s}
.tgcp-tab-btn:hover{background:#eaeaff}
.tgcp-tab-btn.active{background:var(--tgcp-primary);color:#fff;border-color:var(--tgcp-primary)}

.tgcp-tab-content h3,
.tgcp-curriculum h3,
.tgcp-instructors h3,
.tgcp-reviews h3{font-size:var(--tgcp-size-h);font-weight:var(--tgcp-weight-heading);color:#000;text-align:left;margin-bottom:18px}
.tgcp-tab-content p{font-size:var(--tgcp-size-body);font-weight:var(--tgcp-weight-body);color:#000;text-align:left;margin-bottom:18px}

/* curriculum */
.tgcp-curriculum{margin-top:40px}
.tgcp-curriculum-item{border:1px solid #ddd;border-radius:10px;margin-bottom:15px;background:#fff;padding:15px 20px;cursor:pointer;transition:all .3s ease}
.tgcp-curriculum-item:hover{background:#f9f9ff}
.tgcp-curriculum-title{display:flex;justify-content:space-between;font-weight:var(--tgcp-weight-heading);font-size:18px;color:var(--tgcp-primary)}
.tgcp-curriculum-content{margin-top:10px;font-size:var(--tgcp-size-body);font-weight:var(--tgcp-weight-body);color:#000;text-align:left}
.tgcp-arrow{font-size:18px;color:#2f2f2f}

/* instructors */
.tgcp-instructor-grid{display:flex;gap:20px;flex-wrap:wrap}
.tgcp-instructor-card{flex:1 1 30%;border-radius:15px;overflow:hidden;background:#f0f2ff;box-shadow:0 0 8px rgba(0,0,0,.05);text-align:center;display:flex;flex-direction:column;justify-content:space-between}
.tgcp-instructor-img{background:#0c1a66;padding:20px}
.tgcp-instructor-img img{width:100%;max-height:250px;object-fit:contain;border-radius:8px}
.tgcp-instructor-info{padding:15px;background:#f0f2ff;font-size:var(--tgcp-size-body);font-weight:var(--tgcp-weight-strong);color:var(--tgcp-ink);line-height:1.4;text-align:left}

/* reviews */
.tgcp-reviews{margin-top:40px}
.tgcp-review-layout{display:flex;flex-direction:column;gap:30px}

.tgcp-rating-summary{display:flex;align-items:flex-start;gap:20px}
.tgcp-rating-score{font-size:40px;font-weight:var(--tgcp-weight-heading);line-height:1}
.tgcp-rating-stars{position:relative;display:inline-block;line-height:1;color:orange;font-size:18px;margin:5px 0}
.tgcp-stars-outer{color:#ddd}
.tgcp-stars-inner{position:absolute;top:0;left:0;width:var(--percent,0%);overflow:hidden;color:orange;white-space:nowrap;pointer-events:none}
.tgcp-total-rating{font-size:var(--tgcp-size-small);color:#555}

.tgcp-rating-bars{flex:1}
.tgcp-bar-line{display:flex;align-items:center;font-size:var(--tgcp-size-small);margin-bottom:10px}
.tgcp-bar-line .tgcp-bar{flex:1;height:6px;background:#ddd;margin:0 10px;border-radius:5px;overflow:hidden}
.tgcp-bar-line .tgcp-fill{background:var(--tgcp-primary);height:100%}

.tgcp-review-image-box{flex:0 0 55%;background:transparent;display:flex;align-items:center}
.tgcp-review-image-box img{width:100%;max-width:600px;height:300px;border-radius:12px;object-fit:contain}

/* extra imgs */
.tgcp-reviews-gallery{display:flex;gap:16px;margin-top:18px;flex-wrap:wrap}
.tgcp-reviews-gallery img{flex:1 1 calc(33.333% - 11px);max-width:calc(33.333% - 11px);height:240px;object-fit:contain;background:#fff;border:1px solid #e6e6e6;border-radius:12px;box-shadow:0 2px 10px rgba(0,0,0,.05);padding:8px}
@media (max-width:992px){.tgcp-reviews-gallery img{flex-basis:calc(50% - 8px);max-width:calc(50% - 8px);height:180px}}

/* journey (currently unused) */
.tgcp-journey{margin-top:40px;display:flex;justify-content:center;background:#080A22}
.tgcp-journey-image{width:100%;height:auto;object-fit:cover}

/* mobile */
@media (max-width:768px){
  .row{flex-direction:column;gap:40px}
  .tgcp-left{padding-left:0}
  .tgcp-video iframe{height:250px}
}

