  -webkit-overflow-scrolling: touch;
  scrollbar-width: none;
}
.filter-scroll-wrapper::-webkit-scrollbar { display: none; }

/* ============ Test Grid & Cards ============ */
.test-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  color: #cdcfd426;
}

.test-card {
  position: relative;
