  font-family: "Manrope", sans-serif;
}

.test-meta {
  font-size: 14px;
  display: flex;
  gap: 16px;
  color: #05071A;
  margin-bottom: 12px;
  font-weight: 500;
  margin-top: 12px;
}

/* âœ… FINAL Coming Soon State â€” blur + dim + overlay text */
.test-card.soon > * {
  filter: blur(2px);
  opacity: 0.35;
  pointer-events: none;        /* block clicks on children */
}

/* dark glass layer */
.test-card.soon::before {
  content: "";
  position: absolute;
  inset: 0;
  background: rgba(7, 10, 34, 0.55);
  backdrop-filter: blur(2px);
  -webkit-backdrop-filter: blur(2px);
  z-index: 50;
}

/* centered label */
.test-card.soon::after {
  content: "Coming Soon";
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  color: #fff;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.06em;
  font-size: 22px;
  text-shadow: 0 2px 8px rgba(0,0,0,0.35);
