  padding: 6px 14px;
  border-radius: 6px;
  font-size: 13px;
  display: inline-block;
  margin-top: 10px;
  text-align: left;
  font-family: "Manrope", sans-serif;
}

/* ============ CTA Buttons ============ */
.course-buttons { display: flex; gap: 10px; }

