/* Card base must have its own stacking context */
.test-card {
  position: relative;
  isolation: isolate;           /* IMPORTANT for overlay z-index */
  overflow: hidden;
  border-radius: 12px;
  background: #E5E7EB;
}

/* Content wrapper */
.test-card .card-inner {
  position: relative;
  z-index: 0;
}

/* When coming soon */
.test-card.is-soon .card-inner {
  filter: blur(2px);
  opacity: 0.35;                /* thoda dim */
  pointer-events: none;         /* clicks band */
}

/* Overlay layer (poster) */
.soon-overlay {
  position: absolute;
  inset: 0;
  z-index: 2;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(7,10,34,0.55);  /* dark glass */
  backdrop-filter: blur(2px);
  -webkit-backdrop-filter: blur(2px);
  text-align: center;
}

/* Optional: if you want an image instead of pill text */
/*
.soon-overlay .soon-img {
  max-width: 60%;
  height: auto;
  filter: drop-shadow(0 8px 18px rgba(0,0,0,.35));
}
*/

/* Pill text */
.soon-pill {
  padding: 12px 20px;
  border-radius: 999px;
  color: #fff;
  font-weight: 800;
  letter-spacing: .08em;
  text-transform: uppercase;
  border: 2px solid rgba(255,255,255,.6);
  background: rgba(255,255,255,.08);
}

/* Disabled attempt button look */
.attempt-btn:disabled,
.attempt-btn[aria-disabled="true"] {
  opacity: 0.6;
  cursor: not-allowed;
  border-color: #9ca3af;
  color: #9ca3af;
}

