.discussion-forum {
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

.forum-header {
  margin-bottom: 25px;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

.section-header h2 {
