:root{
  --adcp-font:"Manrope","Inter","Segoe UI",system-ui,-apple-system,Roboto,Helvetica,Arial,sans-serif;

  /* weights */
  --adcp-weight-heading:700;
  --adcp-weight-strong:600;
  --adcp-weight-body:500;

  /* sizes */
  --adcp-size-title:clamp(22px,2.0vw,28px);
  --adcp-size-h:clamp(20px,1.9vw,26px);
  --adcp-size-sub:18px;
  --adcp-size-body:16px;
  --adcp-size-small:14px;
  --adcp-line:1.55;

  /* colors */
  --adcp-primary:#1A237E;
  --adcp-accent:#D8583F;
  --adcp-ink:#1d1d1f;
  --adcp-ink-2:#111;
  --adcp-muted:#888;
  --adcp-border:#999;
}

/* RESET / BASE */
*{margin:0;padding:0;box-sizing:border-box}
body{
  font-family:var(--adcp-font);
  font-size:var(--adcp-size-body);
  font-weight:var(--adcp-weight-body);
  line-height:var(--adcp-line);
  background:#f4f6f8;
  color:var(--adcp-ink);
}

.container{max-width:1600px;width:100%;margin-bottom:0}
.row{display:flex;gap:30px}

/* PAGE WRAPPER */
.adcp-page{background:#fff;padding-top:40px}

/* LEFT */
.adcp-left{flex:70%;padding-left:50px}
.adcp-video iframe{
  width:100%;height:400px;border-radius:10px;border:none;margin-bottom:20px
}
.adcp-title{
  text-transform:uppercase;letter-spacing:.4px;
  color:var(--adcp-primary);
  font-size:var(--adcp-size-title);
  font-weight:var(--adcp-weight-heading);
  margin-bottom:25px
}

/* INFO GRID */
.adcp-info-grid{display:flex;gap:0;margin-bottom:30px;flex-wrap:wrap}
.adcp-info-item{display:flex;align-items:center;gap:10px;min-width:220px;flex:1}
.adcp-ico{font-size:22px;color:#005ae0}
.adcp-label{font-size:var(--adcp-size-small);color:var(--adcp-muted);font-weight:var(--adcp-weight-body)}
.adcp-val{font-size:var(--adcp-size-body);color:var(--adcp-ink-2);font-weight:var(--adcp-weight-strong)}

/* RIGHT SIDEBAR */
.adcp-right{flex:0 0 30%;display:flex;flex-direction:column;gap:30px}
.adcp-course-info{
  background:#fff;padding:25px;border-radius:12px;border:1px solid var(--adcp-border);
  box-shadow:0 0 10px rgba(0,0,0,.05)
}
.adcp-course-title{
  background:#EFF0FF;color:var(--adcp-primary);
  font-weight:var(--adcp-weight-heading);
  padding:12px 15px;border-radius:8px;
  font-size:var(--adcp-size-title);
  text-align:center;margin-bottom:25px
}
.adcp-buy-btn{
  width:100%;padding:10px 12px;background:#1d0a71;color:#fff;
  font-weight:var(--adcp-weight-heading);border:none;border-radius:8px;cursor:pointer;
  font-size:var(--adcp-size-body);margin-top:15px;transition:background .3s ease
}
.adcp-buy-btn:hover{background:#140a5a}

.adcp-desc-scroll{max-height:220px;overflow-y:auto;padding-right:5px}
.adcp-desc-scroll::-webkit-scrollbar{width:0;background:transparent}

.adcp-material-box{
  background:#fff;padding:25px;border:1px solid var(--adcp-border);
  border-radius:12px;box-shadow:0 0 10px rgba(0,0,0,.05)
}
.adcp-material-box h4{
  font-size:var(--adcp-size-sub);
  font-weight:var(--adcp-weight-heading);
  color:#000;margin-bottom:12px;text-align:left
}
.adcp-material-list{list-style:disc;text-align:left;padding-left:20px;color:#000;font-size:15px}
.adcp-material-list li{margin-bottom:8px}

/* TABS */
.adcp-tabs{margin-top:40px}
.adcp-tab-buttons{display:flex;gap:15px;margin-bottom:25px;flex-wrap:wrap}
.adcp-tab-btn{
  border:2px solid var(--adcp-primary);background:transparent;color:var(--adcp-primary);
  font-weight:var(--adcp-weight-heading);font-size:var(--adcp-size-body);
  padding:8px 24px;border-radius:16px;cursor:pointer;transition:.3s
}
.adcp-tab-btn:hover{background:#eaeaff}
.adcp-tab-btn.active{background:var(--adcp-primary);color:#fff;border-color:var(--adcp-primary)}

.adcp-tab-content h3,
.adcp-curriculum h3,
.adcp-instructors h3,
.adcp-reviews h3{
  font-size:var(--adcp-size-h);
  font-weight:var(--adcp-weight-heading);
  color:#000;text-align:left;margin-bottom:18px
}
.adcp-tab-content p{
  font-size:var(--adcp-size-body);
  font-weight:var(--adcp-weight-body);
  color:#000;text-align:left;margin-bottom:18px
}

/* CURRICULUM */
.adcp-curriculum{margin-top:40px}
.adcp-curriculum-item{
  border:1px solid #ddd;border-radius:10px;margin-bottom:15px;background:#fff;
  padding:15px 20px;cursor:pointer;transition:all .3s ease
}
.adcp-curriculum-item:hover{background:#f9f9ff}
.adcp-curriculum-title{
  display:flex;justify-content:space-between;
  font-weight:var(--adcp-weight-heading);font-size:18px;color:var(--adcp-primary)
}
.adcp-curriculum-content{margin-top:10px;font-size:var(--adcp-size-body);font-weight:var(--adcp-weight-body);color:#000;text-align:left}
.adcp-arrow{font-size:18px;color:#2f2f2f}

/* INSTRUCTORS */
.adcp-instructor-grid{display:flex;gap:20px;flex-wrap:wrap}
.adcp-instructor-card{
  flex:1 1 30%;border-radius:15px;overflow:hidden;background:#f0f2ff;
  box-shadow:0 0 8px rgba(0,0,0,.05);text-align:center;display:flex;flex-direction:column;justify-content:space-between
}
.adcp-instructor-img{background:#0c1a66;padding:20px}
.adcp-instructor-img img{width:100%;max-height:250px;object-fit:contain;border-radius:8px}
.adcp-instructor-info{
  padding:15px;background:#f0f2ff;font-size:var(--adcp-size-body);
  font-weight:var(--adcp-weight-strong);color:var(--adcp-ink);line-height:1.4;text-align:left
}

/* REVIEWS */
.adcp-reviews{margin-top:40px}
.adcp-review-layout{display:flex;flex-direction:column;gap:30px}

.adcp-rating-summary{display:flex;align-items:flex-start;gap:20px}
.adcp-rating-score{font-size:40px;font-weight:var(--adcp-weight-heading);line-height:1}
.adcp-rating-stars{position:relative;display:inline-block;line-height:1;color:orange;font-size:18px;margin:5px 0}
.adcp-stars-outer{color:#ddd}
.adcp-stars-inner{
  position:absolute;top:0;left:0;width:var(--percent,0%);
  overflow:hidden;color:orange;white-space:nowrap;pointer-events:none
}
.adcp-total-rating{font-size:var(--adcp-size-small);color:#555}

.adcp-rating-bars{flex:1}
.adcp-bar-line{display:flex;align-items:center;font-size:var(--adcp-size-small);margin-bottom:10px}
.adcp-bar-line .adcp-bar{flex:1;height:6px;background:#ddd;margin:0 10px;border-radius:5px;overflow:hidden}
.adcp-bar-line .adcp-fill{background:var(--adcp-primary);height:100%}

.adcp-review-image-box{flex:0 0 55%;background:transparent;display:flex;align-items:center}
.adcp-review-image-box img{width:100%;max-width:600px;height:300px;border-radius:12px;object-fit:contain}

/* review gallery */
.adcp-reviews-gallery{display:flex;gap:16px;margin-top:18px;flex-wrap:wrap}
.adcp-reviews-gallery img{
  flex:1 1 calc(33.333% - 11px);max-width:calc(33.333% - 11px);
  height:240px;object-fit:contain;background:#fff;border:1px solid #e6e6e6;
  border-radius:12px;box-shadow:0 2px 10px rgba(0,0,0,.05);padding:8px
}
@media (max-width:992px){
  .adcp-reviews-gallery img{flex-basis:calc(50% - 8px);max-width:calc(50% - 8px);height:180px}
}

/* MOBILE */
@media (max-width:768px){
  .row{flex-direction:column;gap:40px}
  .adcp-left{padding-left:0}
  .adcp-video iframe{height:250px}
}

