import React, { useState, useEffect } from "react";
import "./IIMPredictor.css"; // Importing CSS for styling
import axios from "axios"; // âœ… Import Axios
import { useNavigate } from "react-router-dom";
import IIMPredictionpage from "../../subpages/IIMPredictionPage/IIMPredictionpage";

import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import jsPDF from "jspdf";

const IIMPredictor = () => {
  const [link, setLink] = useState("");
  // const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  // const [userDetails, setUserDetails] = useState(null);
  // const [htmlContent, setHtmlContent] = useState(null); // âœ… Store HTML for response sheet
  const navigate = useNavigate();
  const [fullHtml, setFullHtml] = useState(""); // âœ… Store full HTML content
  const [questions, setQuestions] = useState([]); // âœ… Store Questions
  const [score, setScore] = useState(null); // âœ… Store Score Data

  const user = JSON.parse(localStorage.getItem("user")); // âœ… Extract User Data
  const userId = user?.id; // âœ… Get userId safely

  const [formData, setFormData] = useState({
    category: "",
    gender: "",
    tenthPercentage: "",
    twelfthPercentage: "",
    discipline: "",
    degree: "",
    graduationPercentage: "",
    workExperience: "",
    takenCAT: "",
    catYear: "",
    interestedCourses: [],
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      setFormData((prev) => ({
        ...prev,
        interestedCourses: Array.isArray(prev.interestedCourses)
          ? checked
            ? [...prev.interestedCourses, value]
            : prev.interestedCourses.filter((course) => course !== value)
          : [value], // âœ… Ensure it remains an array
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      alert("âš ï¸ Please login to submit your details!");
      localStorage.setItem("redirectAfterLogin", "/iim-results");
      localStorage.setItem("formData", JSON.stringify(formData));
      navigate("/login");
      return;
    }

    const requestData = {
      userId: user.id,
      category: formData.category,
      gender: formData.gender,
      classX: formData.tenthPercentage,
      classXII: formData.twelfthPercentage,
      discipline: formData.discipline,
      graduation: formData.degree,
      gradPercentage: formData.graduationPercentage,
      workExperience: formData.workExperience,
      takenCATBefore: formData.takenCAT,
      catYear: formData.catYear,
      interestedCourses: formData.interestedCourses,
    };

    try {
      setLoading(true);
      console.log("ðŸ” Submitting Data:", requestData); // âœ… Debugging

      const response = await axios.post(
        "/api/v2/iim-predictor",
        requestData
      );

      console.log("âœ… API Response:", response.data); // âœ… Debugging

      setLoading(false);

      if (response.status === 200 || response.status === 201) {
        alert("âœ… Form Submitted Successfully!");
        localStorage.setItem(
          `iim-predictor-${user.id}`,
          JSON.stringify(response.data)
        );

        console.log("Navigating to:", `/iim-results/${user.id}`);
        navigate(`/iim-results/${user.id}`);
      }
    } catch (error) {
      setLoading(false);
      console.error(
        "âŒ Error submitting form:",
        error.response?.data || error.message
      );
      alert("âŒ Submission failed. Please try again.");
    }
  };

  useEffect(() => {
    if (!userId) return;

    const storedData = localStorage.getItem(`iim-predictor-${userId}`);
    if (storedData) {
      setFormData(JSON.parse(storedData)); // âœ… Restore saved form data
    }
  }, [userId]);

  // const handleFileChange = (e) => {
  //   setFile(e.target.files[0]);
  // };

  const handleSearch = async () => {
    if (!link) {
        toast.error("âš ï¸ Please provide a valid link.");
        return;
    }

    setLoading(true);
    try {
        const response = await axios.post("/api/v3/fetch-questions", { link });

        if (response.data.fullHtmlContent) {
            setFullHtml(response.data.fullHtmlContent);
        }

        if (response.data.questions && response.data.questions.length > 0) {
            setQuestions([...response.data.questions]); // âœ… Store Questions
            console.log("âœ… Fetched Questions:", response.data.questions); // âœ… Debugging
        } else {
            console.warn("âš ï¸ No questions found in API response.");
            toast.error("âš ï¸ No questions found.");
        }

        toast.success("âœ… Response sheet fetched successfully!");
    } catch (error) {
        console.error("âŒ Error fetching data:", error);
        toast.error("âŒ Failed to fetch response sheet.");
    }
    setLoading(false);
};



const calculateScore = () => {
  console.log("ðŸ“Œ Current Questions Before Scoring:", questions); 

  if (!questions || questions.length === 0) {
      toast.error("âš ï¸ No questions available to calculate score.");
      return;
  }

  let correct = 0, wrong = 0, unattempted = 0;

  questions.forEach(q => {
      console.log("ðŸ” Checking Question:", q);
      console.log("âœ… Chosen Option:", q.chosenOption, " | ðŸŽ¯ Correct Answer:", q.correctAnswer);

      if (q.chosenOption && q.correctAnswer) {
          if (q.chosenOption.trim() === q.correctAnswer.trim()) { // âœ… Trim extra spaces
              correct++;
          } else if (q.chosenOption === "N/A" || q.chosenOption === "") {
              unattempted++;
          } else {
              wrong++;
          }
      }
  });

  const totalScore = correct * 3 - wrong * 1;
  setScore({ correct, wrong, unattempted, total: totalScore });

  console.log("âœ… Score Calculation:", { correct, wrong, unattempted, total: totalScore });
  toast.success("âœ… Score calculated successfully!");
};

const downloadScorecard = () => {
    const doc = new jsPDF();

    // âœ… Set Title
    doc.setFont("helvetica", "bold");
    doc.setFontSize(18);
    doc.text("Student Response Sheet Scorecard", 20, 20);

    // âœ… Set Normal Font
    doc.setFont("helvetica", "normal");
    doc.setFontSize(14);
    doc.text(`âœ… Correct Answers: ${score.correct}`, 20, 40);
    doc.text(`âŒ Wrong Answers: ${score.wrong}`, 20, 50);
    doc.text(`âž– Unattempted: ${score.unattempted}`, 20, 60);
    doc.text(`ðŸŽ¯ Total Score: ${score.total}`, 20, 70);

    // âœ… Save PDF
    doc.save("Response_Sheet_Scorecard.pdf");
    toast.success("âœ… Scorecard downloaded successfully!");
  };

  const handlePrint = () => {
    const printContent = document.querySelector(".response-sheet");

    if (!printContent) {
        toast.error("âš ï¸ No response sheet available to print.");
        return;
    }

    const printWindow = window.open("", "", "width=800,height=600");
    printWindow.document.write("<html><head><title>Response Sheet</title></head><body>");
    printWindow.document.write(printContent.innerHTML);
    printWindow.document.write("</body></html>");
    printWindow.document.close();
    printWindow.print();
};

  return (
    <div>
      <div className="predictor-container">
        <h2 className="predictor-heading">
          ðŸ“œ CMAT/CAT Response Sheet Checker
        </h2>

        {/* âœ… Input for Response Sheet Link */}
        <input
        className="MainInput"
          type="text"
          placeholder="Paste response sheet link"
          value={link}
          onChange={(e) => setLink(e.target.value)}
        />



        {/* âœ… Search Button */}
        <button onClick={handleSearch} disabled={loading}>
          {loading ? "Fetching..." : "Check Response Sheet"}
        </button>

        {/* âœ… Print Button to Download the Response Sheet */}
         <button onClick={handlePrint} className="print-btn">Print</button>


        {/* âœ… Display Full Response Sheet (With Images & Tables) */}
        {fullHtml && (
          <div
            className="response-sheet"
            dangerouslySetInnerHTML={{ __html: fullHtml }}
          />
        )}

        {/* âœ… Calculate Score Button */}
        <button onClick={calculateScore}>Calculate Score</button>

        {/* âœ… Show Scorecard Section */}
        {score && (
    <div className="scorecard-section">
        <h3>ðŸ“œ Scorecard</h3>
        <p>âœ… Correct Answers: <strong>{score.correct}</strong></p>
        <p>âŒ Wrong Answers: <strong>{score.wrong}</strong></p>
        <p>âž– Unattempted: <strong>{score.unattempted}</strong></p>
        <p>ðŸŽ¯ Total Score: <strong>{score.total}</strong></p>

        {/* âœ… Download Scorecard Button */}
        <button onClick={downloadScorecard}>Download Scorecard</button>
    </div>
)}
        <ToastContainer />
      </div>




      <div className="IIM-container">
      <h1>Lets Take Test </h1>
      <p>Test your percentile with our online mock exam.</p>
      <button className="ExamButton" onClick={() => navigate("/exam")}>Start Exam</button>
    </div>






      <div className="predictor-container">
        <h2 className="predictor-heading">LET'S PREDICT YOUR MBA COLLEGE</h2>
        <form className="predictor-form" onSubmit={handleSubmit}>
          {/* Personal Information */}
          <div className="form-group">
            <label>PERSONAL INFORMATION</label>
            <select
              name="category"
              onChange={handleChange}
              value={formData.category}
            >
              <option value="">Category (e.g. OBC)</option>
              <option value="General">General</option>
              <option value="OBC">OBC</option>
              <option value="SC/ST">SC/ST</option>
            </select>
            <select
              name="gender"
              onChange={handleChange}
              value={formData.gender}
            >
              <option value="">Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </div>

          <div className="form-group">
            <label></label>
            <select
              name="discipline"
              onChange={handleChange}
              value={formData.discipline}
            >
              <option value="">Discipline (e.g. Science)</option>
              <option value="BBA">Science</option>
              <option value="BCom">Commerce</option>
              <option value="Engineering">Arts</option>
            </select>

            <input
              type="text"
              name="tenthPercentage"
              placeholder=" 10th Percentage (e.g. 98.72)"
              onChange={handleChange}
              value={formData.tenthPercentage}
            />
            <input
              type="text"
              name="twelfthPercentage"
              placeholder=" 12th Percentage (e.g. 98.72)"
              onChange={handleChange}
              value={formData.twelfthPercentage}
            />
          </div>

          {/* Graduation Details */}
          <div className="form-group">
            <label>GRADUATION</label>
            <select
              name="degree"
              onChange={handleChange}
              value={formData.degree}
            >
              <option value="">Degree (e.g. BBA)</option>
