                âœ•
              </button>
            </div>

            <form
              className="tf-modal-form"
              onSubmit={async (e) => {
                e.preventDefault();
                const fd = new FormData(e.currentTarget);
                try {
                  await http.post('/crm/leads/enquiry', { name: fd.get('name') || 'Anonymous', email: fd.get('email') || '', mobile: fd.get('phone') || '', message: fd.get('question') || '', page: 'FAQ-Modal' });
                  alert('Submitted!');
                  setAskOpen(false);
