.ttp-hero-container {
  background-color: #081c59;
  color: white;
  position: relative;
  overflow: hidden;
  padding: 60px 20px 100px;
}
/* ===== Floating links: segmented 3x2 (like screenshot-2) ===== */
.ttp-floating-links{
  /* grid: 3 columns, no horizontal gap so pills touch */
  display: grid;
  grid-template-columns: repeat(3, minmax(140px, 1fr));
  column-gap: 0;            /* important: pills stick together horizontally */
  row-gap: 12px;            /* space between the two rows */
  justify-content: center;
  align-items: center;
  margin-top: -147px;
  max-width: 720px;
}

/* wrappers disappear so their child <button> become direct grid items */
.btn-pair-1,
.btn-pair-2,
.btn-pair-3{
  display: contents;
}

/* shared button look */
.ttp-floating-links button{
  width: 100%;
  background:#fff;
  color:#081c59;
  border: 2px solid #081c59;
  font-weight: 700;
  font-size: 14px;
  padding: 10px 18px;
  line-height: 1;
  border-radius: 0;         /* default: square; weâ€™ll round edges per position */
  transition: .2s ease;
}

/* hover/focus */
.ttp-floating-links button:hover{
  background:#081c59;
  color:#fff;
}

/* â€”â€” segmented corners (every row has 3 buttons) â€”â€” */
/* first column in each row */
.ttp-floating-links button:nth-child(3n + 1){
  border-top-left-radius: 12px;
  border-bottom-left-radius: 12px;
}
/* center column in each row: remove double borders */
.ttp-floating-links button:nth-child(3n + 2){
  border-left-width: 0;
  border-right-width: 0;
}
/* last column in each row */
.ttp-floating-links button:nth-child(3n){
  border-top-right-radius: 12px;
  border-bottom-right-radius: 12px;
}

/* mobile: thoda sa compact, phir bhi 3 columns rakhte hain
   (agar 2 columns chaho to grid-template-columns: repeat(2, 1fr) kar dena) */
@media (max-width: 420px){
  .ttp-floating-links{
    grid-template-columns: repeat(3, 1fr);
    row-gap: 10px;
  }
  .ttp-floating-links button{
    font-size: 12px;
    padding: 8px 12px;
  }
}

