/* ===== Section wrapper ===== */
.tgc-score{
  background:#070A22;
  /* padding: 32px 0 48px; */
  font-family: 'Manrope', sans-serif;
  color:#fff;
}
.tgc-container{

  margin: 0 auto;
  display:flex;
  gap:28px;
  align-items:flex-start;
  padding: 0 16px;
}

/* ===== Left dark panel with white graph card ===== */
.tgc-graph-area{
  background:#0B0E25;         /* dark panel like screenshot */
  border-radius:16px;
  margin-top: -2px;
  padding:18px;
  flex:0 0 340px;             /* fixed width column */
}
.tgc-graph-box{
  background:#fff;
  border-radius:14px;
  padding:16px 16px 12px;
  text-align:center;
}
.tgc-graph-title{
  color:#ff5e3a;
  font-weight:700;
  font-size:14px;
  margin:0 0 6px;
}
.tgc-graph-subtitle{
  color:#666;
  font-size:12px;
  margin:0 0 10px;
}
.tgcs-image-box{ display:flex; justify-content:center; }
.tgcs-fixed-img{
  width: 260px;
  height: 220px;
  object-fit:contain;
}

/* ===== Right white strip with topper cards ===== */
.tgc-toppers{
  background:#fff;            /* white strip */
  border-radius:16px;
  margin-right: -30px;
  padding:18px;
  flex:1;
  display:flex;
  height: 350px;
  gap:20px;
  overflow-x:auto;            /* horizontal scroll */
  align-items:stretch;
  box-shadow: 0 4px 14px rgba(0,0,0,.08);
  scrollbar-width: none;      /* Firefox hide */
}
.tgc-toppers::-webkit-scrollbar{ display:none; } /* Chrome/Safari hide */

/* single card (transparentâ€”image carries the look) */
.tgc-topper-card{
  flex:0 0 230px;             /* card width like screenshot */
}

/* image with overlayed name */
.tgc-photo{
  position:relative;
  height:240px;
  border-radius:14px;
  overflow:hidden;
}
.tgc-photo img{
  width:100%;
  height:100%;
  object-fit:cover;
  display:block;
}

/* bottom gradient + name (white) just like screenshot */
.tgc-overlay{
  position:absolute; left:0; right:0; bottom:0;
  padding:10px 12px;
  color:#fff; font-weight:600; font-size:14px;
  background: linear-gradient(180deg, rgba(0,0,0,0) 0%,
                                        rgba(0,0,0,.55) 90%);
}

/* score below image */
.tgc-score-text{
  margin-top:10px;
  font-size:20px;
  font-weight:800;
  color:#1b2b8f;              /* deep blue like screenshot */
}

/* ===== Responsive ===== */
@media (max-width: 900px){
  .tgc-graph-area{ flex:0 0 300px; }
  .tgc-photo{ height:220px; }
}
@media (max-width: 600px){
  .tgc-container{ flex-direction:column; }
  .tgc-graph-area{ width:100%; }
  .tgc-toppers{ padding:14px; gap:16px; }
  .tgc-topper-card{ flex-basis:210px; }
  .tgc-photo{ height:210px; }
  .tgc-score-text{ font-size:18px; }
}





